static int metal_irq_allocate(int irq_base, int irq_num)
{
    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;
    FILE *flog = fopen("/tmp/logs/153.txt", "a");

    if (flog) {
        fprintf(flog, "metal_irq_allocate enter: irq_base=%d irq_num=%d\n",
                irq_base, irq_num);
        fflush(flog);
    }

    if (irq_num == 0) {
        if (flog) {
            fprintf(flog, "irq_num == 0 -> return METAL_IRQ_ANY\n");
            fclose(flog);
        }
        return METAL_IRQ_ANY;
    }
    if (irq_tocheck == METAL_IRQ_ANY) {
        irq_tocheck = 0;
        if (flog) {
            fprintf(flog, "irq_tocheck was METAL_IRQ_ANY, set to 0\n");
            fflush(flog);
        }
    }
    irq_end_tocheck = irq_tocheck + irq_num;
    if (flog) {
        fprintf(flog, "initial range: irq_tocheck=%d irq_end_tocheck=%d\n",
                irq_tocheck, irq_end_tocheck);
        fflush(flog);
    }

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        cntr = metal_container_of(node,
                                  struct metal_irq_controller, node);
        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;

        if (flog) {
            fprintf(flog,
                    "iter: cntr=%p cntr->irq_base=%d cntr->irq_num=%d cntr_irq_end=%d\n",
                    (void *)cntr, cntr_irq_base, cntr->irq_num, cntr_irq_end);
            fflush(flog);
        }

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            if (flog) {
                fprintf(flog,
                        "overlap detected: irq_tocheck=%d irq_end_tocheck=%d cntr_irq_base=%d cntr_irq_end=%d\n",
                        irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
                fflush(flog);
            }
            if (irq_base != METAL_IRQ_ANY) {
                if (flog) {
                    fprintf(flog, "requested base overlaps -> return METAL_IRQ_ANY\n");
                    fclose(flog);
                }
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            if (flog) {
                fprintf(flog, "adjusted: irq_tocheck=%d irq_end_tocheck=%d\n",
                        irq_tocheck, irq_end_tocheck);
                fflush(flog);
            }
        } else {
            if (flog) {
                fprintf(flog, "no overlap with this controller\n");
                fflush(flog);
            }
        }
    }

    if (flog) {
        fprintf(flog, "exit: returning irq_tocheck=%d\n", irq_tocheck);
        fclose(flog);
    }
    return irq_tocheck;
}
