#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append formatted message to /tmp/logs/183.txt */
static void __attribute__((unused)) log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/183.txt", "a");
    if (f == NULL) {
        /* If logging is not possible, silently continue to preserve original logic. */
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int metal_irq_register_controller(struct metal_irq_controller *cntr)
{
    int irq_base;
    struct metal_list *node;

    log_probe("enter metal_irq_register_controller: cntr=%p", (void *)cntr);

    if (cntr == NULL) {
        log_probe("cntr is NULL, returning -EINVAL");
        return -EINVAL;
    }

    log_probe("cntr->node address = %p", (void *)&cntr->node);

    metal_list_for_each(&irq_cntrs, node) {
        log_probe("iterating node = %p; comparing with &cntr->node = %p", (void *)node, (void *)&cntr->node);
        if (node == &cntr->node) {
            log_probe("node equals &cntr->node: early return 0");
            return 0;
        }
    }

    /*
     * Allocate IRQ numbers which are not yet used by any IRQ
     * controllers.
     */
    irq_base = metal_irq_allocate(cntr->irq_base, cntr->irq_num);
    log_probe("metal_irq_allocate returned irq_base = %d", irq_base);
    if (irq_base == METAL_IRQ_ANY) {
        log_probe("irq_base == METAL_IRQ_ANY, returning -EINVAL");
        return -EINVAL;
    }
    cntr->irq_base = irq_base;
    log_probe("assigned cntr->irq_base = %d", cntr->irq_base);

    metal_list_add_tail(&irq_cntrs, &cntr->node);
    log_probe("metal_list_add_tail called with node = %p; returning 0", (void *)&cntr->node);
    return 0;
}
