#include <stdio.h>

void metal_linux_bus_finish(void)
{
    struct linux_bus *lbus;
    struct metal_bus *bus;
    FILE *log = fopen("/tmp/logs/283.txt", "a");

    if (log) {
        fprintf(log, "metal_linux_bus_finish: enter\n");
        fflush(log);
    }

    for_each_linux_bus(lbus) {
        /* Log the pointer and name for each linux bus encountered */
        if (log) {
            fprintf(log, "loop: lbus=%p", (void *)lbus);
            if (lbus && lbus->bus_name)
                fprintf(log, " bus_name=%s", lbus->bus_name);
            else
                fprintf(log, " bus_name=(null)");
            fprintf(log, "\n");
            fflush(log);
        }

        /* Preserve original logic: find and unregister if found */
        if (metal_bus_find(lbus->bus_name, &bus) == 0) {
            if (log) {
                fprintf(log, "metal_bus_find: found bus=%p for bus_name=%s, unregistering\n",
                        (void *)bus, lbus->bus_name ? lbus->bus_name : "(null)");
                fflush(log);
            }
            metal_bus_unregister(bus);
        } else {
            if (log) {
                fprintf(log, "metal_bus_find: no bus found for bus_name=%s\n",
                        lbus->bus_name ? lbus->bus_name : "(null)");
                fflush(log);
            }
        }
    }

    if (log) {
        fprintf(log, "metal_linux_bus_finish: exit\n");
        fclose(log);
    }
}
