#include <stdio.h>
#include <stdarg.h>

/* Probe helper that appends human-readable logs to /tmp/logs/283.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/283.txt", "a");
    if (f == NULL) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

void metal_linux_bus_finish(void)
{
    struct linux_bus *lbus;
    struct metal_bus *bus;

    probe_log("ENTER: metal_linux_bus_finish");

    for_each_linux_bus(lbus) {
        /* Log loop entry and lbus state. Avoid dereferencing if NULL. */
        probe_log("LOOP: lbus=%p, lbus->bus_name=%p, bus_name_str=%s",
                  (void *)lbus,
                  (void *)(lbus ? lbus->bus_name : NULL),
                  (lbus && lbus->bus_name) ? lbus->bus_name : "(null)");

        int _ret = metal_bus_find(lbus->bus_name, &bus);
        probe_log("metal_bus_find: name=%s, return=%d, out_bus=%p",
                  (lbus && lbus->bus_name) ? lbus->bus_name : "(null)",
                  _ret,
                  (void *)bus);

        if (_ret == 0) {
            probe_log("ACTION: metal_bus_unregister called for bus=%p (from lbus name=%s)",
                      (void *)bus,
                      (lbus && lbus->bus_name) ? lbus->bus_name : "(null)");
            metal_bus_unregister(bus);
        } else {
            probe_log("SKIP: no registered bus found for name=%s",
                      (lbus && lbus->bus_name) ? lbus->bus_name : "(null)");
        }
    }

    probe_log("EXIT: metal_linux_bus_finish");
}
