#include <sys/mman.h>
#include <sys/stat.h>
#include <sys/file.h>
#include <fcntl.h>
#include <unistd.h>
#include <stdio.h>
#include <errno.h>
#include <stddef.h>

/* Probe: write control flow and state to /tmp/logs/567.txt */
static void metal_map_log(const char *tag, int fd, off_t offset, size_t size,
                          int expand, int flags, int error, const char *extra)
{
    FILE *f = fopen("/tmp/logs/567.txt", "a");
    if (!f)
        return;
    fprintf(f, "%s | fd=%d | offset=%lld | size=%zu | expand=%d | flags=0x%x | error=%d | %s\n",
            tag, fd, (long long)offset, size, expand, flags, error, extra ? extra : "");
    fclose(f);
}

int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Log entry state */
    metal_map_log("enter", fd, offset, size, expand, flags, 0, "start of metal_map");

    /* This is the statement reported as a modification of the parameter 'flags' */
    flags |= MAP_SHARED;
    metal_map_log("after_flags_or", fd, offset, size, expand, flags, 0, "flags |= MAP_SHARED executed");

    if (fd < 0) {
        metal_map_log("branch_fd_lt_0_before", fd, offset, size, expand, flags, 0, "enter fd<0 branch");
        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
        metal_map_log("branch_fd_lt_0_after", fd, offset, size, expand, flags, 0, "fd set to -1 and flags set to MAP_PRIVATE|MAP_ANONYMOUS");
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat statbuf;

        metal_map_log("branch_expand_enter", fd, offset, size, expand, flags, 0, "begin expand handling");

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        metal_map_log("after_flock", fd, offset, size, expand, flags, error, "flock LOCK_EX attempted");
        if (!error)
            error = fstat(fd, &statbuf);
        metal_map_log("after_fstat", fd, offset, size, expand, flags, error, "fstat attempted");
        if (!error && statbuf.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        metal_map_log("after_ftruncate", fd, offset, size, expand, flags, error, "ftruncate attempted if needed");
        if (!error)
            flock(fd, LOCK_UN);
        metal_map_log("after_flock_unlock", fd, offset, size, expand, flags, error, "flock LOCK_UN attempted");
        if (error) {
            metal_map_log("expand_error_return", fd, offset, size, expand, flags, error, "returning due to error in expand path");
            return -errno;
        }
    }

    metal_map_log("before_mmap", fd, offset, size, expand, flags, 0, "about to call mmap");
    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        metal_map_log("mmap_failed", fd, offset, size, expand, flags, -errno, "mmap returned MAP_FAILED");
        return -errno;
    }
    metal_map_log("mmap_success", fd, offset, size, expand, flags, 0, "mmap succeeded");

    *result = mem;
    metal_map_log("exit", fd, offset, size, expand, flags, 0, "returning success");
    return 0;
}
