#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <sys/mman.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <unistd.h>
#include <fcntl.h>
#include <sys/file.h>

static void log_probe(const char *fmt, ...)
{
    int saved_errno = errno;
    FILE *f = fopen("/tmp/logs/563.txt", "a");
    if (f != NULL) {
        va_list ap;
        va_start(ap, fmt);
        vfprintf(f, fmt, ap);
        va_end(ap);
        fputs("\n", f);
        fclose(f);
    }
    errno = saved_errno;
}

int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
          void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    log_probe("enter metal_map: fd=%d, offset=%lld, size=%llu, expand=%d, flags=%d",
              fd, (long long)offset, (unsigned long long)size, expand, flags);

    flags |= MAP_SHARED;

    if (fd < 0) {
        log_probe("branch: fd < 0 (anonymous mapping). original fd=%d", fd);
        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;

        log_probe("branch: expand requested. reqsize=%lld", (long long)reqsize);

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        log_probe("flock(LOCK_EX) result: error=%d, errno=%d", error, errno);
        if (!error)
            error = fstat(fd, &stat);
        log_probe("fstat result: error=%d, st_size=%lld", error, (long long)stat.st_size);
        if (!error && stat.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        log_probe("ftruncate (if needed) result: error=%d", error);
        if (!error)
            flock(fd, LOCK_UN);
        log_probe("unlock after expand: error=%d", error);
        if (error)
            return -errno;
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        log_probe("mmap failed: fd=%d, size=%llu, offset=%lld, errno=%d",
                  fd, (unsigned long long)size, (long long)offset, errno);
        return -errno;
    }
    log_probe("mmap succeeded: mem=%p, fd=%d, size=%llu, offset=%lld",
              mem, fd, (unsigned long long)size, (long long)offset);

    *result = mem;
    log_probe("exit metal_map: returning 0, result=%p", mem);
    return 0;
}
