#include <stdio.h>
#include <string.h>
#include <errno.h>

int metal_shmem_open_generic(const char *name, size_t size,
			     struct metal_io_region **result)
{
	struct metal_generic_shmem *shmem;
	struct metal_list *node;
	FILE *log;

	/* Log entry into the function */
	log = fopen("/tmp/logs/209.txt", "a");
	if (log) {
		fprintf(log, "ENTER: metal_shmem_open_generic name=%p size=%zu result=%p\n",
			(void *)name, size, (void *)result);
		fflush(log);
		fclose(log);
	}

	metal_list_for_each(&_metal.common.generic_shmem_list, node) {
		shmem = metal_container_of(node, struct metal_generic_shmem, node);

		/* Probe: log addresses and nullness of pointers before calling strcmp.
		   Do NOT dereference the name pointers here to avoid introducing any
		   additional out-of-bounds accesses. We flush the log before the
		   strcmp call so that evidence is preserved even if strcmp causes a
		   crash/undefined behaviour (which is the reported violation scenario). */
		log = fopen("/tmp/logs/209.txt", "a");
		if (log) {
			fprintf(log, "ITER: shmem=%p shmem->name=%p name=%p\n",
				(void *)shmem, (void *)(shmem ? shmem->name : NULL), (void *)name);
			if (shmem == NULL) {
				fprintf(log, "ITER: shmem is NULL (unexpected)\n");
			} else if (shmem->name == NULL) {
				fprintf(log, "ITER: shmem->name is NULL\n");
			}
			if (name == NULL) {
				fprintf(log, "ITER: provided name parameter is NULL\n");
			}
			fprintf(log, "ITER: about to call strcmp(shmem->name, name)\n");
			fflush(log);
			fclose(log);
		}

		/* Preserve original behaviour: call strcmp as in the original code. */
		if (strcmp(shmem->name, name) != 0)
			continue;
		if (size <= metal_io_region_size(&shmem->io)) {
			*result = &shmem->io;

			log = fopen("/tmp/logs/209.txt", "a");
			if (log) {
				fprintf(log, "MATCH: shmem=%p selected, io_region=%p, size_ok\n",
					(void *)shmem, (void *)&shmem->io);
				fflush(log);
				fclose(log);
			}
			return 0;
		}
	}

	log = fopen("/tmp/logs/209.txt", "a");
	if (log) {
		fprintf(log, "EXIT: no matching shared memory found -> -ENOENT\n");
		fflush(log);
		fclose(log);
	}

	return -ENOENT;
}
