#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <errno.h>
#include <string.h>
#include <stdint.h>

/* Probe logging helpers. Appends to /tmp/logs/533.txt. */
static FILE *probe_fp = NULL;

static void probe_log_open(void)
{
    if (probe_fp)
        return;
    /* Try to ensure directory exists; ignore errors. */
    (void)mkdir("/tmp/logs", 0777);
    probe_fp = fopen("/tmp/logs/533.txt", "a");
    if (probe_fp) {
        /* Line buffered for timely logs. */
        setvbuf(probe_fp, NULL, _IOLBF, 0);
    }
}

static void probe_log(const char *fmt, ...)
{
    va_list ap;
    probe_log_open();
    if (!probe_fp)
        return;
    va_start(ap, fmt);
    vfprintf(probe_fp, fmt, ap);
    va_end(ap);
    fflush(probe_fp);
}

static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                   struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    probe_log("ENTER: metal_shmem_try_map(ps=%p, fd=%d, size=%zu)\n", (void *)ps, fd, size);

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    probe_log("aligned size=%zu, page_size=%zu, pages=%zu\n",
              size, ps->page_size, pages);

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    probe_log("metal_map(fd=%d, size=%zu, flags=0x%x) -> error=%d, mem=%p\n",
              fd, size, ps->mmap_flags, error, mem);
    if (error) {
        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        probe_log("EXIT: metal_shmem_try_map -> mmap failed (error=%d)\n", error);
        return error;
    }

    error = mlock(mem, size);
    probe_log("mlock(mem=%p, size=%zu) -> %d\n", mem, size, error);
    if (error) {
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    probe_log("malloc phys (%zu bytes) -> %p\n", phys_size, (void *)phys);
    if (!phys) {
        metal_unmap(mem, size);
        probe_log("EXIT: metal_shmem_try_map -> malloc(phys) failed\n");
        return -ENOMEM;
    }

    io = malloc(sizeof(*io));
    probe_log("malloc io (%zu bytes) -> %p\n", sizeof(*io), (void *)io);
    if (!io) {
        free(phys);
        metal_unmap(mem, size);
        probe_log("EXIT: metal_shmem_try_map -> malloc(io) failed\n");
        return -ENOMEM;
    }

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        probe_log("_metal.pagemap_fd < 0, using offsets as PA. phys[0]=%llu\n",
                  (unsigned long long)phys[0]);
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            probe_log("page %zu: virt=%p offset=%zu computed_addr=%p\n",
                      page, (void *)virt, offset, (void *)(virt + offset));

            error = metal_virt2phys(virt + offset, &phys[page]);

            probe_log("metal_virt2phys(addr=%p) -> error=%d, phys[%zu]=%llu\n",
                      (void *)(virt + offset), error, page,
                      (unsigned long long)phys[page]);

            if (error < 0)
                phys[page] = METAL_BAD_OFFSET;

            if (phys[page] == METAL_BAD_OFFSET) {
                probe_log("page %zu: set METAL_BAD_OFFSET\n", page);
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    probe_log("EXIT: metal_shmem_try_map -> success, io=%p, mem=%p, phys=%p, size=%zu\n",
              (void *)io, mem, (void *)phys, size);

    return 0;
}
