suspicious_frags:
OS_BSP_Lock_Impl();

    while (StartPos != EndPos)
    {
        if (StartPos > EndPos)
        {
            /* handle wrap */
            WriteSize = console->BufSize - StartPos;
        }
        else
        {
            WriteSize = EndPos - StartPos;
        }

        OS_BSP_ConsoleOutput_Impl(&console->BufBase[StartPos], WriteSize);

        StartPos += WriteSize;
        if (StartPos >= console->BufSize)
        {
            /* handle wrap */
            StartPos = 0;
        }
    }

    OS_BSP_Unlock_Impl();

if (console->IsAsync)
        {
            /* post the sem for the utility task to run */
            OS_ConsoleWakeup_Impl(&token);
        }
        else
        {
            /* output directly */
            OS_ConsoleOutput_Impl(&token);
        }
