int32 OS_DirRead_Impl(const OS_object_token_t *token, os_dirent_t *dirent)
{
    struct dirent *                de;
    OS_impl_dir_internal_record_t *impl;
    FILE *log = NULL;

    impl = OS_OBJECT_TABLE_GET(OS_impl_dir_table, *token);

    /* Probe: log entry and key pointers/state before calling readdir */
    log = fopen("/tmp/logs/933.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_DirRead_Impl ENTRY: token=%p, impl=%p, impl->dp=%p\n",
                (const void *)token, (const void *)impl, (const void *)impl->dp);
        fclose(log);
        log = NULL;
    }

    /* NOTE - the readdir() call is non-reentrant ....
     * However, this is performed while the global dir table lock is taken.
     * Therefore this ensures that only one such call can occur at any given time.
     *
     * Static analysis tools may warn about this because they do not know
     * this function is externally serialized via the global lock.
     */
    /* cppcheck-suppress readdirCalled */
    /* cppcheck-suppress nonreentrantFunctionsreaddir */
    de = readdir(impl->dp);

    /* Probe: log the result of readdir and (if present) the d_name contents */
    log = fopen("/tmp/logs/933.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "readdir returned de=%p\n", (const void *)de);
        if (de == NULL)
        {
            fprintf(log, "readdir -> NULL, returning OS_ERROR\n");
        }
        else
        {
            /* d_name is an array; print safely (it should be NUL-terminated) */
            fprintf(log, "readdir -> d_name='%s'\n", de->d_name);
        }
        fclose(log);
        log = NULL;
    }

    if (de == NULL)
    {
        return OS_ERROR;
    }

    /* Copy name into provided buffer (preserve original behavior) */
    strncpy(dirent->FileName, de->d_name, sizeof(dirent->FileName) - 1);
    dirent->FileName[sizeof(dirent->FileName) - 1] = 0;

    /* Probe: confirm what was copied into dirent->FileName */
    log = fopen("/tmp/logs/933.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "COPIED: dirent->FileName='%s'\n", dirent->FileName);
        fprintf(log, "OS_DirRead_Impl EXIT: returning OS_SUCCESS\n");
        fclose(log);
        log = NULL;
    }

    return OS_SUCCESS;
}
