int32 OS_DirRead_Impl(const OS_object_token_t *token, os_dirent_t *dirent)
{
    struct dirent *                de;
    OS_impl_dir_internal_record_t *impl;
    impl = OS_OBJECT_TABLE_GET(OS_impl_dir_table, *token);

    /* NOTE - the readdir() call is non-reentrant ....
     * However, this is performed while the global dir table lock is taken.
     * Therefore this ensures that only one such call can occur at any given time.
     *
     * Static analysis tools may warn about this because they do not know
     * this function is externally serialized via the global lock.
     */
    /* cppcheck-suppress readdirCalled */
    /* cppcheck-suppress nonreentrantFunctionsreaddir */
    de = readdir(impl->dp);
    if (de == NULL)
    {
        /* Log the failure path for reproduction/evidence */
        FILE *fp = fopen("/tmp/logs/933.txt", "a");
        if (fp)
        {
            fprintf(fp, "OS_DirRead_Impl: readdir returned NULL; token=%p impl=%p\n", (const void *)token, (const void *)impl);
            fclose(fp);
        }

        return OS_ERROR;
    }

    /* Log the successful readdir return and the contents of the source name. */
    {
        FILE *fp = fopen("/tmp/logs/933.txt", "a");
        if (fp)
        {
            /* de is non-const here in the original code; log its pointer and name to show it is only read. */
            fprintf(fp, "OS_DirRead_Impl: readdir returned de=%p d_name=\"%s\" impl=%p token=%p\n",
                    (const void *)de,
                    (de->d_name != NULL) ? de->d_name : "(null)",
                    (const void *)impl,
                    (const void *)token);
            fclose(fp);
        }
    }

    /* Preserve original copying semantics */
    strncpy(dirent->FileName, de->d_name, sizeof(dirent->FileName) - 1);
    dirent->FileName[sizeof(dirent->FileName) - 1] = 0;

    /* Log the destination buffer after the copy to show what was copied. */
    {
        FILE *fp = fopen("/tmp/logs/933.txt", "a");
        if (fp)
        {
            fprintf(fp, "OS_DirRead_Impl: copied to dirent=%p FileName=\"%s\" size_of_FileName=%zu\n",
                    (const void *)dirent,
                    dirent->FileName,
                    (size_t)sizeof(dirent->FileName));
            fclose(fp);
        }
    }

    return OS_SUCCESS;
}
