#include <stdio.h>
#include <string.h>
#include <stddef.h>

bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    /* Probe: log initial fetch and basic state */
    {
        FILE *log = fopen("/tmp/logs/1753.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_FileSys_FindVirtMountPoint: entry\n");
            fprintf(log, "  filesys_ptr=%p\n", (void *)filesys);
            /* Guard against NULL pointers in logs to avoid undefined behavior */
            fprintf(log, "  target=%s\n", target ? target : "(null)");
            /* flags may be an integer type; print as unsigned for readability */
            fprintf(log, "  flags=0x%08x\n", (unsigned int)filesys->flags);
            fprintf(log, "  virtual_mountpt=\"%s\"\n", filesys->virtual_mountpt);
            fclose(log);
        }
    }

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        /* log branch taken */
        FILE *log = fopen("/tmp/logs/1753.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  check: IS_MOUNTED_VIRTUAL not set -> returning false\n");
            fclose(log);
        }

        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    /* Log after computing mplen */
    {
        FILE *log = fopen("/tmp/logs/1753.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  mplen=%zu, virtual_mountpt_size=%zu\n", mplen, sizeof(filesys->virtual_mountpt));
            fclose(log);
        }
    }

    /*
     * The virtual_mountpt member should be a substring of the search target.
     * If this matches a basic substring check then it may be match
     */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        /* not a substring, so not a match */
        FILE *log = fopen("/tmp/logs/1753.txt", "a");
        if (log != NULL)
        {
            int cmp = 0;
            /* Only call strncmp for logging safely if target is non-NULL. */
            if (target != NULL)
            {
                cmp = strncmp(target, filesys->virtual_mountpt, mplen);
            }
            fprintf(log, "  substring check failed: mplen=%zu, cmp=%d -> returning false\n", mplen, cmp);
            fclose(log);
        }

        return false;
    }

    /*
     * Confirm that the substring ends at either a directory separator
     * or the end of string  (so exact mount points also match).
     *
     * For instance consider a virtual_mountpt of /mnt/abc and searching
     * for target=/mnt/abcd - this should return false in that case.
     */
    {
        int final_result = (target[mplen] == '/' || target[mplen] == 0);

        FILE *log = fopen("/tmp/logs/1753.txt", "a");
        if (log != NULL)
        {
            /* target[mplen] may be zero; print numeric value as well to be unambiguous */
            int ch = (int)target[mplen];
            fprintf(log, "  final check: target[mplen]=%d ('%c') -> returning %d\n", ch,
                    (ch >= 32 && ch < 127) ? (char)ch : '?', final_result);
            fprintf(log, "OS_FileSys_FindVirtMountPoint: exit\n\n");
            fclose(log);
        }

        return (final_result != 0);
    }
}
