#include <stdio.h>
#include <errno.h>
#include <string.h>

/* Probe helper: appends logs to /tmp/logs/843.txt. */
static void OS_GenericRead_LogProbe(const OS_object_token_t *token,
                                    OS_impl_file_internal_record_t *impl,
                                    size_t nbytes,
                                    int32 timeout,
                                    const char *stage,
                                    int32 return_code,
                                    uint32 operation,
                                    ssize_t os_result)
{
    FILE *fp = fopen("/tmp/logs/843.txt", "a");
    if (fp == NULL)
    {
        return; /* best-effort logging only */
    }

    fprintf(fp, "[OS_GenericRead] stage=%s token=%p impl=%p nbytes=%zu timeout=%d return_code=%d operation=0x%08x os_result=%zd\n",
            stage,
            (const void *)token,
            (void *)impl,
            nbytes,
            timeout,
            return_code,
            (unsigned)operation,
            os_result);

    if (impl != NULL)
    {
        /* Log the fields that are relevant to the violation scenario. */
        fprintf(fp, "  impl->selectable=%d impl->fd=%d\n",
                impl->selectable,
                impl->fd);
    }

    if (os_result < 0)
    {
        fprintf(fp, "  errno=%d strerror=%s\n", errno, strerror(errno));
    }

    fflush(fp);
    fclose(fp);
}

int32 OS_GenericRead_Impl(const OS_object_token_t *token, void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    /* Entry probe */
    OS_GenericRead_LogProbe(token, NULL, nbytes, timeout, "entry", -1, 0u, -1);

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Log after obtaining impl pointer (this is key to the reported scenario) */
    OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "after_impl", -1, 0u, -1);

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_READABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "before_select", return_code, operation, -1);
            return_code = OS_SelectSingle_Impl(token, &operation, timeout);
            OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "after_select", return_code, operation, -1);
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_READABLE) != 0)
        {
            OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "before_read", return_code, operation, -1);
            os_result = read(impl->fd, buffer, nbytes);
            OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "after_read", return_code, operation, os_result);

            if (os_result < 0)
            {
                OS_DEBUG("read: %s\n", strerror(errno));
                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                return_code = (int32)os_result;
            }
        }
    }

    /* Final probe before return */
    OS_GenericRead_LogProbe(token, impl, nbytes, timeout, "exit", return_code, (uint32)operation, (ssize_t)return_code);

    return return_code;
}
