#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append a formatted message to the log file. */
static void osal_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1962.txt", "a");
    if (!f)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_ObjectIdFindNextFree(OS_object_token_t *token)
{
    uint32              max_id;
    uint32              base_id;
    uint32              local_id = 0;
    uint32              serial;
    uint32              i;
    int32               return_code;
    OS_common_record_t *obj = NULL;
    OS_objtype_state_t *objtype_state;

    base_id       = OS_GetBaseForObjectType(token->obj_type);
    max_id        = OS_GetMaxForObjectType(token->obj_type);
    objtype_state = &OS_objtype_state[token->obj_type];

    /* Log entry state */
    osal_log_probe("OS_ObjectIdFindNextFree ENTER: token=%p obj_type=%u base_id=%lu max_id=%lu last_id_issued=0x%08lX",
                   (void*)token,
                   (unsigned)token->obj_type,
                   (unsigned long)base_id,
                   (unsigned long)max_id,
                   (unsigned long)objtype_state->last_id_issued);

    if (max_id == 0)
    {
        /* if the max id is zero, then this build of OSAL
         * does not include any support for that object type.
         * Return the "not implemented" to differentiate between
         * this case vs. running out of valid slots  */
        return_code = OS_ERR_NOT_IMPLEMENTED;
        serial      = 0;

        /* Log that we took the not-implemented path and the assigned serial */
        osal_log_probe("max_id == 0: took not-implemented path, return_code=%d serial=%lu",
                       (int)return_code,
                       (unsigned long)serial);
    }
    else
    {
        return_code = OS_ERR_NO_FREE_IDS;
        serial      = OS_ObjectIdToSerialNumber_Impl(objtype_state->last_id_issued);

        osal_log_probe("max_id > 0: initialized search, return_code=%d start_serial=%lu",
                       (int)return_code,
                       (unsigned long)serial);
    }

    for (i = 0; i < max_id; ++i)
    {
        local_id = (++serial) % max_id;
        if (serial >= OS_OBJECT_INDEX_MASK)
        {
            /* reset to beginning of ID space */
            serial = local_id;
        }

        obj = &OS_common_table[local_id + base_id];

        /* Log each iteration check */
        osal_log_probe("loop iter %lu/%lu: i=%lu local_id=%lu serial=%lu obj=%p active_id=0x%08lX",
                       (unsigned long)(i + 1),
                       (unsigned long)max_id,
                       (unsigned long)i,
                       (unsigned long)local_id,
                       (unsigned long)serial,
                       (void*)obj,
                       (unsigned long)obj->active_id);

        if (!OS_ObjectIdDefined(obj->active_id))
        {
            return_code = OS_SUCCESS;
            osal_log_probe("free slot found at local_id=%lu serial=%lu: returning SUCCESS",
                           (unsigned long)local_id,
                           (unsigned long)serial);
            break;
        }
    }

    if (return_code == OS_SUCCESS)
    {
        token->obj_idx = OSAL_INDEX_C(local_id);
        OS_ObjectIdCompose_Impl(token->obj_type, serial, &token->obj_id);

        /* Ensure any data in the record has been cleared */
        obj->active_id  = token->obj_id;
        obj->name_entry = NULL;
        obj->creator    = OS_TaskGetId();
        obj->refcount   = 0;

        /* preemptively update the last id issued */
        objtype_state->last_id_issued = token->obj_id;

        osal_log_probe("success-path: token->obj_idx=%ld token->obj_id=0x%08lX obj=%p",
                       (long)token->obj_idx,
                       (unsigned long)token->obj_id,
                       (void*)obj);
    }

    if (return_code != OS_SUCCESS)
    {
        token->obj_idx = OSAL_INDEX_C(-1);
        token->obj_id  = OS_OBJECT_ID_UNDEFINED;

        osal_log_probe("failure-path: return_code=%d token->obj_idx=%ld token->obj_id=0x%08lX",
                       (int)return_code,
                       (long)token->obj_idx,
                       (unsigned long)token->obj_id);
    }

    osal_log_probe("OS_ObjectIdFindNextFree EXIT: return_code=%d", (int)return_code);

    return return_code;
}
