#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: appends messages to /tmp/logs/2343.txt */
static void OS_QueuePut_Log(const char *fmt, ...)
{
    FILE *fp = fopen("/tmp/logs/2343.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(fp, fmt, ap);
    va_end(ap);

    fputc('\n', fp);
    fclose(fp);
}

int32 OS_QueuePut(osal_id_t queue_id, const void *data, size_t size, uint32 flags)
{
    OS_object_token_t           token;
    int32                       return_code;
    OS_queue_internal_record_t *queue;

    /* Log entry and parameters */
    OS_QueuePut_Log("OS_QueuePut ENTRY: queue_id=%lu, data=%p, size=%zu, flags=%lu",
                     (unsigned long)queue_id, data, size, (unsigned long)flags);

    /* Check Parameters */
    OS_CHECK_POINTER(data);
    OS_CHECK_SIZE(size);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, queue_id, &token);

    OS_QueuePut_Log("OS_ObjectIdGetById returned %ld, token=%lu",
                     (long)return_code, (unsigned long)token);

    if (return_code == OS_SUCCESS)
    {
        queue = OS_OBJECT_TABLE_GET(OS_queue_table, token);

        OS_QueuePut_Log("Resolved queue pointer: %p", (void *)queue);

        if (queue != NULL)
        {
            /* Log the queue internal state that is relevant to the violation scenario */
            OS_QueuePut_Log("queue->max_size = %lu; provided size = %zu",
                             (unsigned long)queue->max_size, size);
        }
        else
        {
            OS_QueuePut_Log("Warning: resolved queue pointer is NULL");
        }

        if (size > queue->max_size)
        {
            /*
            ** The buffer that the user is passing in is too large
            */
            OS_QueuePut_Log("Decision: size > queue->max_size -> returning OS_QUEUE_INVALID_SIZE");
            return_code = OS_QUEUE_INVALID_SIZE;
        }
        else
        {
            OS_QueuePut_Log("Decision: size <= queue->max_size -> calling OS_QueuePut_Impl");
            return_code = OS_QueuePut_Impl(&token, data, size, flags);
            OS_QueuePut_Log("OS_QueuePut_Impl returned %ld", (long)return_code);
        }
    }
    else
    {
        OS_QueuePut_Log("OS_ObjectIdGetById failed, skipping queue access, return_code=%ld", (long)return_code);
    }

    OS_QueuePut_Log("OS_QueuePut EXIT: return_code=%ld", (long)return_code);

    return return_code;
}
