#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdint.h>

/* Probe logging helper - appends logs to /tmp/logs/2422.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2422.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputs("\n", f);
    fclose(f);
}

int32 OS_SocketAccept(osal_id_t sock_id, osal_id_t *connsock_id, OS_SockAddr_t *Addr, int32 timeout)
{
    OS_common_record_t *         sock_record;
    OS_common_record_t *         conn_record;
    OS_stream_internal_record_t *sock;
    OS_stream_internal_record_t *conn;
    OS_object_token_t            sock_token;
    OS_object_token_t            conn_token;
    int32                        return_code;

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);
    OS_CHECK_POINTER(connsock_id);

    probe_log("OS_SocketAccept: entry sock_id=%lu timeout=%d Addr=%p connsock_id=%p",
              (unsigned long)sock_id, (int)timeout, (void *)Addr, (void *)connsock_id);

    /*
     * Note: setting "connrecord" here avoids a false warning
     * from static analysis tools about the value being
     * possibly used uninitialized (it cannot be, because
     * return_code is checked, and return_code is only
     * set to OS_SUCCESS when connrecord is also initialized)
     */
    conn_record = NULL;
    probe_log("Assigned conn_record = NULL (initialization)");

    sock_record = NULL;
    sock        = NULL;
    conn        = NULL;
    memset(&sock_token, 0, sizeof(sock_token));
    memset(&conn_token, 0, sizeof(conn_token));

    probe_log("Initial state: sock_record=%p sock=%p conn=%p sock_token=%p conn_token=%p",
              (void *)sock_record, (void *)sock, (void *)conn, (void *)&sock_token, (void *)&conn_token);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, sock_id, &sock_token);
    probe_log("OS_ObjectIdGetById returned %d", (int)return_code);

    if (return_code == OS_SUCCESS)
    {
        sock_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, sock_token);
        sock        = OS_OBJECT_TABLE_GET(OS_stream_table, sock_token);

        probe_log("After lookup: sock_record=%p sock=%p (socket_type=%d stream_state=0x%08x)",
                  (void *)sock_record, (void *)sock,
                  sock ? (int)sock->socket_type : -1,
                  sock ? (unsigned int)sock->stream_state : 0u);

        if (sock->socket_type != OS_SocketType_STREAM)
        {
            /* Socket must be of the STREAM variety */
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
            probe_log("Error: incorrect socket type (%d)", (int)sock->socket_type);
        }
        else if ((sock->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != OS_STREAM_STATE_BOUND)
        {
            /* Socket must be bound but not connected */
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
            probe_log("Error: incorrect socket state (stream_state=0x%08x)", (unsigned int)sock->stream_state);
        }
        else
        {
            /* Now create a unique ID for the connection */
            return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, NULL, &conn_token);
            probe_log("OS_ObjectIdAllocateNew returned %d", (int)return_code);

            if (return_code == OS_SUCCESS)
            {
                conn_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, conn_token);
                conn        = OS_OBJECT_TABLE_GET(OS_stream_table, conn_token);

                probe_log("Allocated conn_token=%p conn_record=%p conn=%p",
                          (void *)&conn_token, (void *)conn_record, (void *)conn);

                /* Incr the refcount to record the fact that an operation is pending on this */
                memset(conn, 0, sizeof(OS_stream_internal_record_t));
                probe_log("Zeroed conn record at %p", (void *)conn);

                conn->socket_domain = sock->socket_domain;
                conn->socket_type   = sock->socket_type;

                probe_log("Copied socket_domain=%d socket_type=%d to conn",
                          (int)conn->socket_domain, (int)conn->socket_type);

                OS_SocketAddrInit_Impl(Addr, sock->socket_domain);
                probe_log("Initialized Addr for domain %d (Addr=%p)", (int)sock->socket_domain, (void *)Addr);

                return_code = OS_SocketAccept_Impl(&sock_token, &conn_token, Addr, timeout);
                probe_log("OS_SocketAccept_Impl returned %d", (int)return_code);

                if (return_code == OS_SUCCESS)
                {
                    /* Generate an entry name based on the remote address */
                    OS_CreateSocketName(&conn_token, Addr, sock_record->name_entry);
                    conn_record->name_entry = conn->stream_name;
                    conn->stream_state |= OS_STREAM_STATE_CONNECTED;

                    probe_log("Accept success: conn_record=%p conn->stream_name=%s conn_record->name_entry=%s",
                              (void *)conn_record,
                              conn ? conn->stream_name : (char *)"(null)",
                              conn_record ? conn_record->name_entry : (char *)"(null)");
                }

                return_code = OS_ObjectIdFinalizeNew(return_code, &conn_token, connsock_id);
                probe_log("OS_ObjectIdFinalizeNew returned %d, connsock_id=%p (*connsock_id=%lu)",
                          (int)return_code, (void *)connsock_id, connsock_id ? (unsigned long)(*connsock_id) : 0ul);
            }
            else
            {
                probe_log("Allocation failed: conn_token not assigned, conn_record remains %p", (void *)conn_record);
            }
        }

        OS_ObjectIdRelease(&sock_token);
        probe_log("Released sock_token=%p", (void *)&sock_token);
    }
    else
    {
        probe_log("OS_ObjectIdGetById failed, returning %d", (int)return_code);
    }

    probe_log("OS_SocketAccept: exit return_code=%d conn_record=%p", (int)return_code, (void *)conn_record);

    return return_code;
}
