#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helper - writes simple log lines to /tmp/logs/2442.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2442.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int32 OS_SocketBindAddress(osal_id_t sock_id, const OS_SockAddr_t *Addr)
{
    OS_common_record_t *         record;
    OS_stream_internal_record_t *stream;
    OS_object_token_t            token;
    int32                        return_code;

    /* Log entry and parameters */
    probe_log("ENTER OS_SocketBindAddress: sock_id=%ld, Addr=%p", (long)sock_id, (void *)Addr);

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, sock_id, &token);
    probe_log("OS_ObjectIdGetById returned %ld", (long)return_code);

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);

        probe_log("token acquired; record=%p, stream=%p", (void *)record, (void *)stream);

        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            /* Not a socket */
            probe_log("branch: Not a socket (socket_domain == OS_SocketDomain_INVALID)");
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else if ((stream->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != 0)
        {
            /* Socket must be neither bound nor connected */
            probe_log("branch: Incorrect obj state (stream_state=0x%08x)", (unsigned)stream->stream_state);
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            probe_log("branch: Calling OS_SocketBindAddress_Impl with Addr=%p", (void *)Addr);
            return_code = OS_SocketBindAddress_Impl(&token, Addr);

            probe_log("OS_SocketBindAddress_Impl returned %ld", (long)return_code);

            if (return_code == OS_SUCCESS)
            {
                OS_CreateSocketName(&token, Addr, NULL);
                probe_log("OS_CreateSocketName called; stream->stream_name=%s", stream->stream_name ? stream->stream_name : "(null)");

                record->name_entry = stream->stream_name;
                probe_log("record->name_entry set to %p", (void *)record->name_entry);

                stream->stream_state |= OS_STREAM_STATE_BOUND;
                probe_log("stream state updated to 0x%08x", (unsigned)stream->stream_state);
            }
            else
            {
                probe_log("OS_SocketBindAddress_Impl failed with %ld", (long)return_code);
            }
        }

        OS_ObjectIdRelease(&token);
        probe_log("token released");
    }
    else
    {
        probe_log("OS_ObjectIdGetById failed; returning %ld", (long)return_code);
    }

    probe_log("EXIT OS_SocketBindAddress: return_code=%ld", (long)return_code);
    return return_code;
}
