#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

/* Helper probe function to append logs for reproducing the violation scenario. */
static void OS_SocketRecvFrom_ProbeLog(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1122.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_SocketRecvFrom_Impl(const OS_object_token_t *token, void *buffer, size_t buflen, OS_SockAddr_t *RemoteAddr,
                             int32 timeout)
{
    int32                           return_code;
    int                             os_result;
    int                             waitflags;
    uint32                          operation;
    struct sockaddr *               sa;
    socklen_t                       addrlen;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Log entry with key state information */
    OS_SocketRecvFrom_ProbeLog("ENTER: token=%p impl=%p buflen=%zu timeout=%d RemoteAddr=%p", (const void *)token, (const void *)impl, buflen, timeout, (const void *)RemoteAddr);

    if (RemoteAddr == NULL)
    {
        sa      = NULL;
        addrlen = 0;
    }
    else
    {
        addrlen = OS_SOCKADDR_MAX_LEN;
        sa      = (struct sockaddr *)&RemoteAddr->AddrData;
    }

    operation = OS_STREAM_STATE_READABLE;
    /*
     * If "O_NONBLOCK" flag is set then use select()
     * Note this is the only way to get a correct timeout
     */
    if (impl->selectable)
    {
        waitflags   = MSG_DONTWAIT;
        return_code = OS_SelectSingle_Impl(token, &operation, timeout);

        /* Probe select result */
        OS_SocketRecvFrom_ProbeLog("AFTER_SELECT: impl->fd=%d selectable=1 operation=0x%08x return_code=%d timeout=%d", impl->fd, (unsigned)operation, return_code, timeout);
    }
    else
    {
        if (timeout == 0)
        {
            waitflags = MSG_DONTWAIT;
        }
        else
        {
            /* note timeout will not be honored if >0 */
            waitflags = 0;
        }
        return_code = OS_SUCCESS;

        /* Probe non-select branch */
        OS_SocketRecvFrom_ProbeLog("AFTER_SELECT: impl->fd=%d selectable=0 waitflags=%d timeout=%d return_code=%d", impl->fd, waitflags, timeout, return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        if ((operation & OS_STREAM_STATE_READABLE) == 0)
        {
            OS_SocketRecvFrom_ProbeLog("TIMEOUT: operation=0x%08x -> timeout", (unsigned)operation);
            return_code = OS_ERROR_TIMEOUT;
        }
        else
        {
            os_result = recvfrom(impl->fd, buffer, buflen, waitflags, sa, &addrlen);

            /* Log the recvfrom call parameters and result */
            OS_SocketRecvFrom_ProbeLog("RECVFROM_CALL: fd=%d buflen=%zu waitflags=%d sa=%p addrlen(in)=%u -> os_result=%d errno=%d (%s)",
                                      impl->fd, buflen, waitflags, (const void *)sa, (unsigned)addrlen, os_result, errno, strerror(errno));

            if (os_result < 0)
            {
                if (errno == EAGAIN || errno == EWOULDBLOCK)
                {
                    OS_SocketRecvFrom_ProbeLog("RECVFROM_EAGAIN: errno=%d (%s)", errno, strerror(errno));
                    return_code = OS_QUEUE_EMPTY;
                }
                else
                {
                    /* Preserve original debug call (may be a no-op) */
                    OS_DEBUG("recvfrom: %s\n", strerror(errno));

                    /* Add an explicit probe log so this executed path is observable */
                    OS_SocketRecvFrom_ProbeLog("RECVFROM_ERROR: fd=%d errno=%d errstr=%s", impl->fd, errno, strerror(errno));

                    return_code = OS_ERROR;
                }
            }
            else
            {
                return_code = os_result;

                if (RemoteAddr != NULL)
                {
                    RemoteAddr->ActualLength = addrlen;
                    OS_SocketRecvFrom_ProbeLog("RECVFROM_SUCCESS: bytes=%d addrlen(out)=%u RemoteAddr=%p", os_result, (unsigned)addrlen, (const void *)RemoteAddr);
                }
                else
                {
                    OS_SocketRecvFrom_ProbeLog("RECVFROM_SUCCESS: bytes=%d addrlen(out)=%u RemoteAddr=NULL", os_result, (unsigned)addrlen);
                }
            }
        }
    }

    OS_SocketRecvFrom_ProbeLog("EXIT: return_code=%d", return_code);
    return return_code;
}
