#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper: append textual logs to /tmp/logs/1783.txt */
static void OS_TranslatePath_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1783.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    OS_TranslatePath_Log("ENTER OS_TranslatePath: VirtualPath=%p LocalPath=%p", (void *)VirtualPath, (void *)LocalPath);
    if (VirtualPath != NULL)
    {
        OS_TranslatePath_Log("VirtualPath content start: '%s'", VirtualPath);
    }

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    OS_TranslatePath_Log("VirtPathLen=%zu (limit=%d)", VirtPathLen, OS_MAX_PATH_LEN);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        OS_TranslatePath_Log("ERROR: path too long");
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to see if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    OS_TranslatePath_Log("name_ptr (last '/')=%p", (void *)name_ptr);
    if (name_ptr == NULL)
    {
        OS_TranslatePath_Log("ERROR: no '/' in VirtualPath");
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    OS_TranslatePath_Log("name_ptr after +1=%p (points to '%c')", (void *)name_ptr, *name_ptr);
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        OS_TranslatePath_Log("ERROR: filename too long (no NUL within %d bytes)", OS_MAX_FILE_NAME);
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        OS_TranslatePath_Log("ERROR: VirtualPath does not start with '/': first_char='%c'", VirtualPath[0]);
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    OS_TranslatePath_Log("Calling OS_ObjectIdGetBySearch with (void *)VirtualPath cast: VirtualPath=%p (content='%s')",
                         (void *)VirtualPath, VirtualPath);

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    OS_TranslatePath_Log("OS_ObjectIdGetBySearch returned %d", return_code);

    if (return_code != OS_SUCCESS)
    {
        OS_TranslatePath_Log("ERROR: OS_ObjectIdGetBySearch failed -> setting return_code=OS_FS_ERR_PATH_INVALID");
        return_code = OS_FS_ERR_PATH_INVALID;
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);
        OS_TranslatePath_Log("OS_ObjectIdGetBySearch: token=%p filesys=%p flags=0x%x",
                             (void *)&token, (void *)filesys, (unsigned)filesys->flags);

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            OS_TranslatePath_Log("Mounted system: system_mountpt='%s' len=%zu, virtual_mountpt='%s' len=%zu",
                                 filesys->system_mountpt, SysMountPointLen,
                                 filesys->virtual_mountpt, VirtPathBegin);
            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
                OS_TranslatePath_Log("Copied system_mountpt to LocalPath (len=%zu)", SysMountPointLen);
            }
        }
        else
        {
            OS_TranslatePath_Log("ERROR: filesystem not mounted system -> incorrect state");
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }

        OS_ObjectIdRelease(&token);
        OS_TranslatePath_Log("Released token");
    }

    if (return_code == OS_SUCCESS)
    {
        OS_TranslatePath_Log("Proceeding to build local path: VirtPathLen=%zu VirtPathBegin=%zu SysMountPointLen=%zu",
                             VirtPathLen, VirtPathBegin, SysMountPointLen);
        if (VirtPathLen < VirtPathBegin)
        {
            OS_TranslatePath_Log("ERROR: VirtPathLen < VirtPathBegin -> invalid path");
            return_code = OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                OS_TranslatePath_Log("Final LocalPath='%s' (len=%zu)", LocalPath, SysMountPointLen + VirtPathLen);
            }
            else
            {
                OS_TranslatePath_Log("ERROR: final local path too long (needed=%zu limit=%d)", SysMountPointLen + VirtPathLen, OS_MAX_LOCAL_PATH_LEN);
                return_code = OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    OS_TranslatePath_Log("EXIT OS_TranslatePath with return_code=%d", return_code);
    return return_code;
}
