#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper - appends to /tmp/logs/1843.txt. Keeps behavior unchanged. */
static void OS_unmount_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1843.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_unmount(const char *mountpoint)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;

    /* Log entry and parameter state */
    OS_unmount_log("OS_unmount ENTRY: mountpoint=%p, mountpoint_str='%s'", (const void *)mountpoint,
                   (mountpoint != NULL) ? mountpoint : "(null)");

    /* Check parameters */
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);

    OS_unmount_log("After OS_CHECK_STRING");

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    OS_unmount_log("OS_ObjectIdGetBySearch returned: %ld", (long)return_code);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        OS_unmount_log("Found filesys pointer=%p, flags=0x%08lx", (void *)filesys, (unsigned long)filesys->flags);

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            OS_unmount_log("Filesys in incorrect state: flags=0x%08lx", (unsigned long)filesys->flags);
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            OS_unmount_log("Calling OS_FileSysUnmountVolume_Impl for token at %p", (void *)&token);
            return_code = OS_FileSysUnmountVolume_Impl(&token);
            OS_unmount_log("OS_FileSysUnmountVolume_Impl returned: %ld", (long)return_code);
        }

        if (return_code == OS_SUCCESS)
        {
            /* mark as mounted in the local table.
             * For now this does both sides (system and virtual) */
            OS_unmount_log("Clearing mount flags (before=0x%08lx)", (unsigned long)filesys->flags);
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);
            OS_unmount_log("Clearing mount flags (after=0x%08lx)", (unsigned long)filesys->flags);
        }

        OS_ObjectIdRelease(&token);
        OS_unmount_log("Released token");
    }

    if (return_code != OS_SUCCESS)
    {
        OS_unmount_log("OS_unmount preparing to return error (mapping to OS_ERR_NAME_NOT_FOUND). original_code=%ld", (long)return_code);
        return_code = OS_ERR_NAME_NOT_FOUND;
    }

    OS_unmount_log("OS_unmount EXIT: return_code=%ld", (long)return_code);

    return return_code;
}
