void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();

        /* Open a probe log for capturing control flow/state for the violation scenario. */
        FILE *probe_log = fopen("/tmp/logs/3389.txt", "a");
        if (probe_log)
        {
            fprintf(probe_log, "---- UT_BSP_DoText ENTER (MessageType=%u) ----\n", (unsigned)MessageType);
            fprintf(probe_log, "Initial MsgEnabled=%u, Initial TermModeBits=0x%X\n", (unsigned)MsgEnabled, (unsigned)TermModeBits);
        }

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                break;
            case UTASSERT_CASETYPE_BEGIN:
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                break;
            default:
                break;
        }

        if (probe_log)
        {
            fprintf(probe_log, "Post-switch TermModeBits=0x%X\n", (unsigned)TermModeBits);
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        /*
         * Probe: capture the pointer returned by UtAssert_GetCaseTypeAbbrev before calling snprintf
         * and then capture the Buffer contents after snprintf. This helps reproduce evidence that
         * snprintf with %%s may have read past the end of the pointed-to object.
         */
        const char *case_abbrev = UtAssert_GetCaseTypeAbbrev(MessageType);
        if (probe_log)
        {
            fprintf(probe_log, "CaseTypeAbbrev ptr=%p\n", (const void *)case_abbrev);
        }

        int snprintf_ret = snprintf(Buffer, sizeof(Buffer), "[%5s]", case_abbrev);

        if (probe_log)
        {
            fprintf(probe_log, "snprintf returned=%d\n", snprintf_ret);
            /* Log Buffer as a C string (what will be output) */
            /* Use a defensive check: Buffer is a local array and is NUL-terminated by snprintf as long as sizeof>0 */
            fprintf(probe_log, "Buffer (as string) = \"%s\"\n", Buffer);
            /* Also log the raw bytes of Buffer to show any non-printable contents */
            fprintf(probe_log, "Buffer bytes:");
            for (size_t i = 0; i < sizeof(Buffer); ++i)
            {
                fprintf(probe_log, " %02X", (unsigned char)Buffer[i]);
            }
            fprintf(probe_log, "\n");
        }

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
        }

        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);
        MsgLen = strlen(OutputMessage);

        if (probe_log)
        {
            fprintf(probe_log, "OutputMessage ptr=%p, MsgLen=%zu\n", (const void *)OutputMessage, MsgLen);
            /* Log up to the first 64 bytes of OutputMessage in hex to help detect missing NUL terminator */
            fprintf(probe_log, "OutputMessage bytes:");
            for (size_t i = 0; i < MsgLen && i < 64; ++i)
            {
                fprintf(probe_log, " %02X", (unsigned char)OutputMessage[i]);
            }
            if (MsgLen > 64)
            {
                fprintf(probe_log, " ... (truncated)");
            }
            fprintf(probe_log, "\n");
        }

        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        if (probe_log)
        {
            fprintf(probe_log, "---- UT_BSP_DoText EXIT (MessageType=%u) ----\n\n", (unsigned)MessageType);
            fclose(probe_log);
        }

        UT_BSP_Unlock();
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        OS_BSP_Shutdown_Impl();
    }
}
