void *UT_Stub_GetReturnValuePtr(UT_EntryKey_t FuncKey, size_t ReturnSize, const char *TypeName)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ReturnPtr;
    FILE *logf = NULL;

    ReturnPtr = NULL;
    StubPtr   = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);

    /* Open log file for append. If it fails, continue without logging. */
    logf = fopen("/tmp/logs/3203.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "UT_Stub_GetReturnValuePtr ENTER\n");
        fprintf(logf, "  FuncKey: %p\n", (void *)&FuncKey);
        fprintf(logf, "  Requested ReturnSize: %lu\n", (unsigned long)ReturnSize);
        fprintf(logf, "  Requested TypeName: %s\n", (TypeName != NULL) ? TypeName : "(null)");
        fprintf(logf, "  Obtained StubPtr: %p\n", (void *)StubPtr);
    }

    if (StubPtr == NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "  StubPtr is NULL -> calling UtAssert_Abort\n");
            fclose(logf);
            logf = NULL;
        }

        /* This shouldn't happen, it means the stub tried to use a
         * return buffer that does not exist.
         *
         * It is most likely caused by a mismatch/incompatibility between
         * stub and handler.  Aborting now is better than segfaulting later,
         * as the errored call should still be on the stack trace
         */
        UtAssert_Abort("Return buffer invalid");
    }
    else
    {
        ReturnPtr = StubPtr->Data.Buff.BasePtr;

        if (logf != NULL)
        {
            fprintf(logf, "  StubPtr->Data.Buff.BasePtr: %p\n", ReturnPtr);
            fprintf(logf, "  StubPtr->Data.Buff.Position: %lu\n", (unsigned long)StubPtr->Data.Buff.Position);
            fprintf(logf, "  StubPtr->Data.Buff.ContentTypeStr: %s\n",
                    (StubPtr->Data.Buff.ContentTypeStr != NULL) ? StubPtr->Data.Buff.ContentTypeStr : "(null)");
        }

        /*
         * Sanity check on the size and type name.
         *
         * In a generated stub these should always match, but if any patches
         * were done or if a hand-written stub is used, mistakes could be made.
         */
        if (StubPtr->Data.Buff.Position != ReturnSize || strcmp(TypeName, StubPtr->Data.Buff.ContentTypeStr) != 0)
        {
            if (logf != NULL)
            {
                fprintf(logf,
                        "  Return value mismatch: expected %s(%lu) got %s(%lu) -- calling UtAssert_Failed\n",
                        (TypeName != NULL) ? TypeName : "(null)", (unsigned long)ReturnSize,
                        (StubPtr->Data.Buff.ContentTypeStr != NULL) ? StubPtr->Data.Buff.ContentTypeStr : "(null)",
                        (unsigned long)StubPtr->Data.Buff.Position);
                fclose(logf);
                logf = NULL;
            }

            UtAssert_Failed("Return value mismatch, expected %s(%lu) got %s(%lu)", TypeName, (unsigned long)ReturnSize,
                            StubPtr->Data.Buff.ContentTypeStr, (unsigned long)StubPtr->Data.Buff.Position);
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "  Return value matched expectations\n");
                fprintf(logf, "  Returning pointer: %p\n", ReturnPtr);
                fclose(logf);
                logf = NULL;
            }
        }
    }

    return ReturnPtr;
}
