#include <stdio.h>
#include <stddef.h>

/* Probe logging helper - appends state information to /tmp/logs/2899.txt */
static void UtAssert_LogState(const char *Stage, const char *String1, size_t String1Max, const char *String2,
                              size_t String2Max, const char *EndPtr1, const char *EndPtr2, size_t FormatLen1,
                              size_t FormatLen2)
{
    FILE *F = fopen("/tmp/logs/2899.txt", "a");
    if (F != NULL)
    {
        fprintf(F, "[%s] String1=%p String1Max=%zu String2=%p String2Max=%zu EndPtr1=%p EndPtr2=%p FormatLen1=%zu FormatLen2=%zu\n",
                Stage, (void *)String1, String1Max, (void *)String2, String2Max, (void *)EndPtr1, (void *)EndPtr2,
                FormatLen1, FormatLen2);

        /* Warn if FormatLen values exceed the local scrubbed buffer size (256) */
        if (FormatLen1 > 255u)
        {
            fprintf(F, "[%s] WARNING: FormatLen1 (%zu) exceeds ScrubbedString1 capacity (255)\n", Stage, FormatLen1);
        }
        if (FormatLen2 > 255u)
        {
            fprintf(F, "[%s] WARNING: FormatLen2 (%zu) exceeds ScrubbedString2 capacity (255)\n", Stage, FormatLen2);
        }

        /* Provide extra hint about potential memchr usage */
        if ((String2 != NULL) && (EndPtr2 == NULL) && (String2Max != (size_t)UTASSERT_STRINGBUF_NULL_TERM))
        {
            fprintf(F, "[%s] HINT: String2 had no NUL within String2Max (%zu) and EndPtr2 is NULL -> FormatLen2 set to String2Max\n",
                    Stage, String2Max);
        }

        fclose(F);
    }
}

bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Initial input state */
    UtAssert_LogState("ENTRY", String1, String1Max, String2, String2Max, NULL, NULL, 0u, 0u);

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }

    /* Log after determining EndPtr1/FormatLen1 */
    UtAssert_LogState("AFTER_END1", String1, String1Max, String2, String2Max, EndPtr1, NULL, FormatLen1, 0u);

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }

    /* Log after determining EndPtr2/FormatLen2 - this is critical for the reported violation */
    UtAssert_LogState("AFTER_END2", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            Compare = memcmp(String1, String2, FormatLen2);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = FormatLen1 - FormatLen2;
        }
    }

    /* Log comparison outcome and lengths used for memcmp */
    UtAssert_LogState("AFTER_COMPARE", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
        }

        /* Log just before copying into ScrubbedString1 */
        UtAssert_LogState("BEFORE_COPY1", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
        }

        /* Log just before copying into ScrubbedString2 - key probe for overflow evidence */
        UtAssert_LogState("BEFORE_COPY2", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

        memcpy(ScrubbedString2, String2, FormatLen2);
    }

    /* Log immediately before writing the terminating NUL to ScrubbedString2 */
    UtAssert_LogState("BEFORE_NUL2", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

    ScrubbedString2[FormatLen2] = 0;

    /* Final state log */
    UtAssert_LogState("EXIT", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
