#include <stdio.h>
#include <stddef.h>

/* Probe logger: record relevant pointers/sizes/state to /tmp/logs/2899.txt */
static void UtAssert_LogProbe(const char *stage, const char *String1, size_t String1Max, const char *String2,
                              size_t String2Max, const char *EndPtr1, const char *EndPtr2, size_t FormatLen1,
                              size_t FormatLen2, unsigned long long Line, const char *File)
{
    FILE *fp = fopen("/tmp/logs/2899.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    fprintf(fp, "---- UtAssert_StringBufCompare LOG (%s) ----\n", stage);
    fprintf(fp, "File: %s Line: %llu\n", (File != NULL) ? File : "(null)", Line);
    fprintf(fp, "String1 ptr: %p, String1Max: %zu, EndPtr1: %p, FormatLen1: %zu\n",
            (void *)String1, String1Max, (void *)EndPtr1, FormatLen1);
    fprintf(fp, "String2 ptr: %p, String2Max: %zu, EndPtr2: %p, FormatLen2: %zu\n",
            (void *)String2, String2Max, (void *)EndPtr2, FormatLen2);

    if (String1 == NULL)
    {
        fprintf(fp, "NOTE: String1 is NULL\n");
    }
    else
    {
        fprintf(fp, "NOTE: String1 is non-NULL (not dereferencing contents to avoid UB)\n");
    }

    if (String2 == NULL)
    {
        fprintf(fp, "NOTE: String2 is NULL\n");
    }
    else
    {
        fprintf(fp, "NOTE: String2 is non-NULL (not dereferencing contents to avoid UB)\n");
    }

    /* Highlight the specific scenario that can lead to memchr being invoked with a NULL pointer or
     * with a length that may exceed the real object bounds. */
    if (EndPtr2 == NULL && FormatLen2 > 0)
    {
        fprintf(fp,
                "POTENTIAL VIOLATION SCENARIO: EndPtr2 == NULL and FormatLen2 > 0.\n"
                "This means the subsequent memchr(String2, '\\n', FormatLen2) call may be invoked with:\n");
        if (String2 == NULL)
        {
            fprintf(fp, "  - String2 == NULL (memchr will be passed a NULL pointer)\n");
        }
        else
        {
            fprintf(fp,
                    "  - String2 != NULL but FormatLen2 (%zu) may exceed the actual object size (memchr may read beyond bounds)\n",
                    FormatLen2);
        }
    }

    fprintf(fp, "---- END LOG (%s) ----\n\n", stage);
    fclose(fp);
}

bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }

    /* Log state after initial end-pointer/format length computation */
    UtAssert_LogProbe("AFTER_ENDPTR_COMPUTE", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2,
                      FormatLen1, FormatLen2, (unsigned long long)Line, File);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            Compare = memcmp(String1, String2, FormatLen2);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = FormatLen1 - FormatLen2;
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */

    /* Log state just before newline scanning and copying (this is where the reported memchr issue occurs) */
    UtAssert_LogProbe("BEFORE_NEWLINE_AND_COPY", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2,
                      FormatLen1, FormatLen2, (unsigned long long)Line, File);

    if (FormatLen1 > 0)
    {
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    /* Final state log after copying/scrubbing */
    UtAssert_LogProbe("AFTER_SCRUB_COPY", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2,
                      FormatLen1, FormatLen2, (unsigned long long)Line, File);

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: \'%s\' == \'%s\'", ScrubbedString1,
                      ScrubbedString2);
}
