    va_list args;
    FILE *f = fopen("/tmp/logs/72.txt", "a");
    if (!f) {
        return;
    }
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    fprintf(f, "\n");
    va_end(args);
    fclose(f);
}

