#include <stdio.h>
#include <stdarg.h>

static void lfs_probe_printf(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/232.txt", "a");
    if (!f) return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static lfs_stag_t lfs_dir_fetchmatch(lfs_t *lfs,
        lfs_mdir_t *dir, const lfs_block_t pair[2],
        lfs_tag_t fmask, lfs_tag_t ftag, uint16_t *id,
        int (*cb)(void *data, lfs_tag_t tag, const void *buffer), void *data) {
    // we can find tag very efficiently during a fetch, since we're already
    // scanning the entire directory
    lfs_stag_t besttag = -1;

    lfs_probe_printf("ENTER lfs_dir_fetchmatch: pair[0]=%llu pair[1]=%llu fmask=0x%llx ftag=0x%llx",
            (unsigned long long)pair[0], (unsigned long long)pair[1],
            (unsigned long long)fmask, (unsigned long long)ftag);

    // if either block address is invalid we return LFS_ERR_CORRUPT here,
    // otherwise later writes to the pair could fail
    if (lfs->block_count 
            && (pair[0] >= lfs->block_count || pair[1] >= lfs->block_count)) {
        lfs_probe_printf("INVALID_PAIR: block_count=%llu pair0=%llu pair1=%llu",
                (unsigned long long)lfs->block_count,
                (unsigned long long)pair[0], (unsigned long long)pair[1]);
        return LFS_ERR_CORRUPT;
    }

    // find the block with the most recent revision
    uint32_t revs[2] = {0, 0};
    int r = 0;
    for (int i = 0; i < 2; i++) {
        int err = lfs_bd_read(lfs,
                NULL, &lfs->rcache, sizeof(revs[i]),
                pair[i], 0, &revs[i], sizeof(revs[i]));
        revs[i] = lfs_fromle32(revs[i]);

        lfs_probe_printf("revs read: i=%d err=%d rev=%u pair=%llu",
                i, err, (unsigned int)revs[i], (unsigned long long)pair[i]);

        if (err && err != LFS_ERR_CORRUPT) {
            lfs_probe_printf("RETURN err from revs read: err=%d i=%d", err, i);
            return err;
        }

        if (err != LFS_ERR_CORRUPT &&
                lfs_scmp(revs[i], revs[(i+1)%2]) > 0) {
            r = i;
            lfs_probe_printf("choose rev index r=%d (i=%d)", r, i);
        }
    }

    dir->pair[0] = pair[(r+0)%2];
    dir->pair[1] = pair[(r+1)%2];
    dir->rev = revs[(r+0)%2];
    dir->off = 0; // nonzero = found some commits

    lfs_probe_printf("after rev selection: r=%d dir.pair[0]=%llu dir.pair[1]=%llu dir.rev=%u",
            r, (unsigned long long)dir->pair[0], (unsigned long long)dir->pair[1], (unsigned int)dir->rev);

    // now scan tags to fetch the actual dir and find possible match
    for (int i = 0; i < 2; i++) {
        lfs_off_t off = 0;
        lfs_tag_t ptag = 0xffffffff;

        uint16_t tempcount = 0;
        lfs_block_t temptail[2] = {LFS_BLOCK_NULL, LFS_BLOCK_NULL};
        bool tempsplit = false;
        lfs_stag_t tempbesttag = besttag;

        // assume not erased until proven otherwise
        bool maybeerased = false;
        bool hasfcrc = false;
        struct lfs_fcrc fcrc;

        dir->rev = lfs_tole32(dir->rev);
        uint32_t crc = lfs_crc(0xffffffff, &dir->rev, sizeof(dir->rev));
        dir->rev = lfs_fromle32(dir->rev);

        lfs_probe_printf("scan start for pair[0]=%llu (iteration i=%d)", (unsigned long long)dir->pair[0], i);

        while (true) {
            // extract next tag
            lfs_tag_t tag;
            off += lfs_tag_dsize(ptag);
            int err = lfs_bd_read(lfs,
                    NULL, &lfs->rcache, lfs->cfg->block_size,
                    dir->pair[0], off, &tag, sizeof(tag));

            lfs_probe_printf("read tag: off=%llu err=%d ptag=0x%llx",
                    (unsigned long long)off, err, (unsigned long long)ptag);

            if (err) {
                lfs_probe_printf("read tag -> err branch: err=%d (checking for CORRUPT)", err);
                if (err == LFS_ERR_CORRUPT) {
                    lfs_probe_printf("read tag -> CORRUPT: breaking out of tag scan at off=%llu", (unsigned long long)off);
                    // can't continue?
                    break;
                }
                lfs_probe_printf("read tag -> RETURN err=%d", err);
                return err;
            }

            crc = lfs_crc(crc, &tag, sizeof(tag));
            tag = lfs_frombe32(tag) ^ ptag;

            // next commit not yet programmed?
            if (!lfs_tag_isvalid(tag)) {
                // we only might be erased if the last tag was a crc
                maybeerased = (lfs_tag_type2(ptag) == LFS_TYPE_CCRC);
                lfs_probe_printf("tag not valid at off=%llu maybeerased=%d ptag=0x%llx", (unsigned long long)off, (int)maybeerased, (unsigned long long)ptag);
                break;
            // out of range?
            } else if (off + lfs_tag_dsize(tag) > lfs->cfg->block_size) {
                lfs_probe_printf("tag out of range at off=%llu block_size=%llu", (unsigned long long)off, (unsigned long long)lfs->cfg->block_size);
                break;
            }

            ptag = tag;

            if (lfs_tag_type2(tag) == LFS_TYPE_CCRC) {
                // check the crc attr
                uint32_t dcrc;
                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag), &dcrc, sizeof(dcrc));
                if (err) {
                    lfs_probe_printf("read ccrc dcrc -> err=%d at off=%llu", err, (unsigned long long)off);
                    if (err == LFS_ERR_CORRUPT) {
                        lfs_probe_printf("read ccrc -> CORRUPT breaking at off=%llu", (unsigned long long)off);
                        break;
                    }
                    lfs_probe_printf("read ccrc -> RETURN err=%d", err);
                    return err;
                }
                dcrc = lfs_fromle32(dcrc);

                if (crc != dcrc) {
                    lfs_probe_printf("crc mismatch: crc=0x%08x dcrc=0x%08x at off=%llu", crc, dcrc, (unsigned long long)off);
                    break;
                }

                // reset the next bit if we need to
                ptag ^= (lfs_tag_t)(lfs_tag_chunk(tag) & 1U) << 31;

                // toss our crc into the filesystem seed for
                // pseudorandom numbers, note we use another crc here
                // as a collection function because it is sufficiently
                // random and convenient
                lfs->seed = lfs_crc(lfs->seed, &crc, sizeof(crc));

                // update with what's found so far
                besttag = tempbesttag;
                dir->off = off + lfs_tag_dsize(tag);
                dir->etag = ptag;
                dir->count = tempcount;
                dir->tail[0] = temptail[0];
                dir->tail[1] = temptail[1];
                dir->split = tempsplit;

                lfs_probe_printf("found CCRC: besttag=0x%llx dir.off=%llu dir.count=%u",
                        (unsigned long long)besttag, (unsigned long long)dir->off, (unsigned int)dir->count);

                // reset crc, hasfcrc
                crc = 0xffffffff;
                continue;
            }

            // crc the entry first, hopefully leaving it in the cache
            err = lfs_bd_crc(lfs,
                    NULL, &lfs->rcache, lfs->cfg->block_size,
                    dir->pair[0], off+sizeof(tag),
                    lfs_tag_dsize(tag)-sizeof(tag), &crc);
            if (err) {
                lfs_probe_printf("bd_crc -> err=%d at off=%llu", err, (unsigned long long)off);
                if (err == LFS_ERR_CORRUPT) {
                    lfs_probe_printf("bd_crc -> CORRUPT breaking at off=%llu", (unsigned long long)off);
                    break;
                }
                lfs_probe_printf("bd_crc -> RETURN err=%d", err);
                return err;
            }

            // directory modification tags?
            if (lfs_tag_type1(tag) == LFS_TYPE_NAME) {
                // increase count of files if necessary
                if (lfs_tag_id(tag) >= tempcount) {
                    tempcount = lfs_tag_id(tag) + 1;
                    lfs_probe_printf("TYPE_NAME: updated tempcount=%u tag_id=%u", (unsigned int)tempcount, (unsigned int)lfs_tag_id(tag));
                }
            } else if (lfs_tag_type1(tag) == LFS_TYPE_SPLICE) {
                tempcount += lfs_tag_splice(tag);

                if (tag == (LFS_MKTAG(LFS_TYPE_DELETE, 0, 0) |
                        (LFS_MKTAG(0, 0x3ff, 0) & tempbesttag))) {
                    tempbesttag |= 0x80000000;
                    lfs_probe_printf("TYPE_SPLICE: delete detected, tempbesttag=0x%llx", (unsigned long long)tempbesttag);
                } else if (tempbesttag != -1 &&
                        lfs_tag_id(tag) <= lfs_tag_id(tempbesttag)) {
                    tempbesttag += LFS_MKTAG(0, lfs_tag_splice(tag), 0);
                    lfs_probe_printf("TYPE_SPLICE: adjusted tempbesttag=0x%llx", (unsigned long long)tempbesttag);
                }
            } else if (lfs_tag_type1(tag) == LFS_TYPE_TAIL) {
                tempsplit = (lfs_tag_chunk(tag) & 1);

                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag), &temptail, 8);
                if (err) {
                    lfs_probe_printf("read TAIL -> err=%d at off=%llu", err, (unsigned long long)off);
                    if (err == LFS_ERR_CORRUPT) {
                        lfs_probe_printf("read TAIL -> CORRUPT breaking at off=%llu", (unsigned long long)off);
                        break;
                    }
                    lfs_probe_printf("read TAIL -> RETURN err=%d", err);
                    return err;
                }
                lfs_pair_fromle32(temptail);
                lfs_probe_printf("TYPE_TAIL: tempsplit=%d temptail=[%llu,%llu]", (int)tempsplit, (unsigned long long)temptail[0], (unsigned long long)temptail[1]);
            } else if (lfs_tag_type3(tag) == LFS_TYPE_FCRC) {
                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag),
                        &fcrc, sizeof(fcrc));
                if (err) {
                    lfs_probe_printf("read FCRC -> err=%d at off=%llu", err, (unsigned long long)off);
                    if (err == LFS_ERR_CORRUPT) {
                        lfs_probe_printf("read FCRC -> CORRUPT breaking at off=%llu", (unsigned long long)off);
                        break;
                    }
                }

                lfs_fcrc_fromle32(&fcrc);
                hasfcrc = true;
                lfs_probe_printf("FCRC found: hasfcrc=%d fcrc.crc=0x%08x", (int)hasfcrc, (unsigned int)fcrc.crc);
            }

            // found a match for our fetcher?
            if ((fmask & tag) == (fmask & ftag)) {
                lfs_probe_printf("candidate match at off=%llu tag=0x%llx", (unsigned long long)off, (unsigned long long)tag);
                int res = cb(data, tag, &(struct lfs_diskoff){
                        dir->pair[0], off+sizeof(tag)});
                lfs_probe_printf("cb returned res=%d for tag=0x%llx", res, (unsigned long long)tag);
                if (res < 0) {
                    if (res == LFS_ERR_CORRUPT) {
                        lfs_probe_printf("cb -> CORRUPT breaking at off=%llu", (unsigned long long)off);
                        break;
                    }
                    lfs_probe_printf("cb -> RETURN err=%d", res);
                    return res;
                }

                if (res == LFS_CMP_EQ) {
                    // found a match
                    tempbesttag = tag;
                    lfs_probe_printf("cb CMP_EQ -> tempbesttag set to 0x%llx", (unsigned long long)tempbesttag);
                } else if ((LFS_MKTAG(0x7ff, 0x3ff, 0) & tag) ==
                        (LFS_MKTAG(0x7ff, 0x3ff, 0) & tempbesttag)) {
                    // found an identical tag, but contents didn't match
                    // this must mean that our besttag has been overwritten
                    tempbesttag = -1;
                    lfs_probe_printf("identical tag but content differ -> tempbesttag cleared");
                } else if (res == LFS_CMP_GT &&
                        lfs_tag_id(tag) <= lfs_tag_id(tempbesttag)) {
                    // found a greater match, keep track to keep things sorted
                    tempbesttag = tag | 0x80000000;
                    lfs_probe_printf("cb CMP_GT -> tempbesttag updated to 0x%llx", (unsigned long long)tempbesttag);
                }
            }
        }

        // found no valid commits?
        if (dir->off == 0) {
            lfs_probe_printf("no valid commits found on dir->pair[0]=%llu, trying other block", (unsigned long long)dir->pair[0]);
            // try the other block?
            lfs_pair_swap(dir->pair);
            dir->rev = revs[(r+1)%2];
            continue;
        }

        // did we end on a valid commit? we may have an erased block
        dir->erased = false;
        if (maybeerased && dir->off % lfs->cfg->prog_size == 0) {
        #ifdef LFS_MULTIVERSION
            // note versions < lfs2.1 did not have fcrc tags, if
            // we're < lfs2.1 treat missing fcrc as erased data
            //
            // we don't strictly need to do this, but otherwise writing
            // to lfs2.0 disks becomes very inefficient
            if (lfs_fs_disk_version(lfs) < 0x00020001) {
                dir->erased = true;

            } else
        #endif
            if (hasfcrc) {
                // check for an fcrc matching the next prog's erased state, if
                // this failed most likely a previous prog was interrupted, we
                // need a new erase
                uint32_t fcrc_ = 0xffffffff;
                int err = lfs_bd_crc(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], dir->off, fcrc.size, &fcrc_);
                if (err && err != LFS_ERR_CORRUPT) {
                    lfs_probe_printf("bd_crc for fcrc -> RETURN err=%d", err);
                    return err;
                }

                // found beginning of erased part?
                dir->erased = (fcrc_ == fcrc.crc);
                lfs_probe_printf("fcrc check -> fcrc_=%u fcrc.crc=%u dir->erased=%d", fcrc_, fcrc.crc, (int)dir->erased);
            }
        }

        // synthetic move
        if (lfs_gstate_hasmovehere(&lfs->gdisk, dir->pair)) {
            if (lfs_tag_id(lfs->gdisk.tag) == lfs_tag_id(besttag)) {
                besttag |= 0x80000000;
                lfs_probe_printf("gstate has move here and equals besttag -> besttag=0x%llx", (unsigned long long)besttag);
            } else if (besttag != -1 &&
                    lfs_tag_id(lfs->gdisk.tag) < lfs_tag_id(besttag)) {
                besttag -= LFS_MKTAG(0, 1, 0);
                lfs_probe_printf("gstate move -> besttag decremented to 0x%llx", (unsigned long long)besttag);
            }
        }

        // found tag? or found best id?
        if (id) {
            *id = lfs_min(lfs_tag_id(besttag), dir->count);
            lfs_probe_printf("setting id result -> id=%u (besttag_id=%u dir.count=%u)", (unsigned int)*id, (unsigned int)lfs_tag_id(besttag), (unsigned int)dir->count);
        }

        if (lfs_tag_isvalid(besttag)) {
            lfs_probe_printf("RETURN besttag=0x%llx", (unsigned long long)besttag);
            return besttag;
        } else if (lfs_tag_id(besttag) < dir->count) {
            lfs_probe_printf("RETURN LFS_ERR_NOENT because besttag_id=%u < dir.count=%u", (unsigned int)lfs_tag_id(besttag), (unsigned int)dir->count);
            return LFS_ERR_NOENT;
        } else {
            lfs_probe_printf("RETURN 0 (no match and no error)");
            return 0;
        }
    }

    lfs_probe_printf("Corrupted dir pair at {0x%x, 0x%x} - final corrupt return", (unsigned int)dir->pair[0], (unsigned int)dir->pair[1]);
    LFS_ERROR("Corrupted dir pair at {0x%"PRIx32", 0x%"PRIx32"}",
            dir->pair[0], dir->pair[1]);
    return LFS_ERR_CORRUPT;
}
