#include <stdio.h>
#include <stdarg.h>

static void lfs_probe_log(const char *fmt, ...){
    FILE *f = fopen("/tmp/logs/238.txt", "a");
    if (!f) return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static lfs_stag_t lfs_dir_fetchmatch(lfs_t *lfs,
        lfs_mdir_t *dir, const lfs_block_t pair[2],
        lfs_tag_t fmask, lfs_tag_t ftag, uint16_t *id,
        int (*cb)(void *data, lfs_tag_t tag, const void *buffer), void *data) {
    // we can find tag very efficiently during a fetch, since we're already
    // scanning the entire directory
    lfs_stag_t besttag = -1;

    lfs_probe_log("ENTER lfs_dir_fetchmatch: lfs=%p dir=%p pair=[%u,%u] fmask=0x%08x ftag=0x%08x id=%p cb=%p",
            (void*)lfs, (void*)dir, (unsigned)pair[0], (unsigned)pair[1], (unsigned)fmask, (unsigned)ftag, (void*)id, (void*)cb);

    // if either block address is invalid we return LFS_ERR_CORRUPT here,
    // otherwise later writes to the pair could fail
    if (lfs->block_count 
            && (pair[0] >= lfs->block_count || pair[1] >= lfs->block_count)) {
        lfs_probe_log("INVALID_PAIR corrupt: block_count=%u pair=[%u,%u]",
                (unsigned)lfs->block_count, (unsigned)pair[0], (unsigned)pair[1]);
        return LFS_ERR_CORRUPT;
    }

    // find the block with the most recent revision
    uint32_t revs[2] = {0, 0};
    int r = 0;
    for (int i = 0; i < 2; i++) {
        int err = lfs_bd_read(lfs,
                NULL, &lfs->rcache, sizeof(revs[i]),
                pair[i], 0, &revs[i], sizeof(revs[i]));
        revs[i] = lfs_fromle32(revs[i]);
        lfs_probe_log("read rev[%d]=%u err=%d", i, (unsigned)revs[i], err);
        if (err && err != LFS_ERR_CORRUPT) {
            lfs_probe_log("ERROR reading rev[%d]: err=%d", i, err);
            return err;
        }

        if (err != LFS_ERR_CORRUPT &&
                lfs_scmp(revs[i], revs[(i+1)%2]) > 0) {
            r = i;
        }
    }

    lfs_probe_log("chosen r=%d revs=[%u,%u]", r, (unsigned)revs[0], (unsigned)revs[1]);

    dir->pair[0] = pair[(r+0)%2];
    dir->pair[1] = pair[(r+1)%2];
    dir->rev = revs[(r+0)%2];
    dir->off = 0; // nonzero = found some commits

    lfs_probe_log("dir set pair=[%u,%u] rev=%u", (unsigned)dir->pair[0], (unsigned)dir->pair[1], (unsigned)dir->rev);

    // now scan tags to fetch the actual dir and find possible match
    for (int i = 0; i < 2; i++) {
        lfs_off_t off = 0;
        lfs_tag_t ptag = 0xffffffff;

        uint16_t tempcount = 0;
        lfs_block_t temptail[2] = {LFS_BLOCK_NULL, LFS_BLOCK_NULL};
        bool tempsplit = false;
        lfs_stag_t tempbesttag = besttag;

        // assume not erased until proven otherwise
        bool maybeerased = false;
        bool hasfcrc = false;
        struct lfs_fcrc fcrc;

        dir->rev = lfs_tole32(dir->rev);
        uint32_t crc = lfs_crc(0xffffffff, &dir->rev, sizeof(dir->rev));
        dir->rev = lfs_fromle32(dir->rev);

        lfs_probe_log("BEGIN scan i=%d dir->pair=[%u,%u] off=%lld", i, (unsigned)dir->pair[0], (unsigned)dir->pair[1], (long long)off);

        while (true) {
            // extract next tag
            lfs_tag_t tag;
            off += lfs_tag_dsize(ptag);
            int err = lfs_bd_read(lfs,
                    NULL, &lfs->rcache, lfs->cfg->block_size,
                    dir->pair[0], off, &tag, sizeof(tag));
            if (err) {
                lfs_probe_log("bd_read tag err=%d off=%lld", err, (long long)off);
                if (err == LFS_ERR_CORRUPT) {
                    // can't continue?
                    lfs_probe_log("bd_read CORRUPT at off=%lld", (long long)off);
                    break;
                }
                lfs_probe_log("ERROR return from bd_read: %d", err);
                return err;
            }

            crc = lfs_crc(crc, &tag, sizeof(tag));
            tag = lfs_frombe32(tag) ^ ptag;

            lfs_probe_log("READ tag raw=0x%08x afterxor=0x%08x ptag=0x%08x crc=0x%08x off=%lld",
                    (unsigned)tag, (unsigned)tag, (unsigned)ptag, (unsigned)crc, (long long)off);

            // next commit not yet programmed?
            if (!lfs_tag_isvalid(tag)) {
                // we only might be erased if the last tag was a crc
                maybeerased = (lfs_tag_type2(ptag) == LFS_TYPE_CCRC);
                lfs_probe_log("tag not valid; maybeerased=%d ptag=0x%08x", (int)maybeerased, (unsigned)ptag);
                break;
            // out of range?
            } else if (off + lfs_tag_dsize(tag) > lfs->cfg->block_size) {
                lfs_probe_log("tag out of range: off + dsize = %lld > block_size=%u", (long long)(off + lfs_tag_dsize(tag)), (unsigned)lfs->cfg->block_size);
                break;
            }

            ptag = tag;

            if (lfs_tag_type2(tag) == LFS_TYPE_CCRC) {
                // check the crc attr
                uint32_t dcrc;
                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag), &dcrc, sizeof(dcrc));
                if (err) {
                    lfs_probe_log("bd_read CCRC dcrc err=%d off=%lld", err, (long long)(off+sizeof(tag)));
                    if (err == LFS_ERR_CORRUPT) {
                        break;
                    }
                }
                dcrc = lfs_fromle32(dcrc);

                lfs_probe_log("CCRC found dcrc=0x%08x computed_crc=0x%08x", (unsigned)dcrc, (unsigned)crc);

                if (crc != dcrc) {
                    lfs_probe_log("CCRC mismatch: crc=0x%08x dcrc=0x%08x -> break", (unsigned)crc, (unsigned)dcrc);
                    break;
                }

                // reset the next bit if we need to
                ptag ^= (lfs_tag_t)(lfs_tag_chunk(tag) & 1U) << 31;

                // toss our crc into the filesystem seed for
                // pseudorandom numbers, note we use another crc here
                // as a collection function because it is sufficiently
                // random and convenient
                lfs->seed = lfs_crc(lfs->seed, &crc, sizeof(crc));

                // update with what's found so far
                besttag = tempbesttag;
                dir->off = off + lfs_tag_dsize(tag);
                dir->etag = ptag;
                dir->count = tempcount;
                dir->tail[0] = temptail[0];
                dir->tail[1] = temptail[1];
                dir->split = tempsplit;

                lfs_probe_log("CCRC commit: besttag=0x%08x dir->off=%lld dir->count=%u", (unsigned)besttag, (long long)dir->off, (unsigned)dir->count);

                // reset crc, hasfcrc
                crc = 0xffffffff;
                continue;
            }

            // crc the entry first, hopefully leaving it in the cache
            err = lfs_bd_crc(lfs,
                    NULL, &lfs->rcache, lfs->cfg->block_size,
                    dir->pair[0], off+sizeof(tag),
                    lfs_tag_dsize(tag)-sizeof(tag), &crc);
            if (err) {
                lfs_probe_log("bd_crc err=%d off=%lld", err, (long long)(off+sizeof(tag)));
                if (err == LFS_ERR_CORRUPT) {
                    break;
                }
                lfs_probe_log("ERROR return from bd_crc: %d", err);
                return err;
            }

            // directory modification tags?
            if (lfs_tag_type1(tag) == LFS_TYPE_NAME) {
                // increase count of files if necessary
                if (lfs_tag_id(tag) >= tempcount) {
                    tempcount = lfs_tag_id(tag) + 1;
                }
                lfs_probe_log("NAME tag id=%u tempcount=%u", (unsigned)lfs_tag_id(tag), (unsigned)tempcount);
            } else if (lfs_tag_type1(tag) == LFS_TYPE_SPLICE) {
                tempcount += lfs_tag_splice(tag);

                if (tag == (LFS_MKTAG(LFS_TYPE_DELETE, 0, 0) |
                        (LFS_MKTAG(0, 0x3ff, 0) & tempbesttag))) {
                    tempbesttag |= 0x80000000;
                } else if (tempbesttag != -1 &&
                        lfs_tag_id(tag) <= lfs_tag_id(tempbesttag)) {
                    tempbesttag += LFS_MKTAG(0, lfs_tag_splice(tag), 0);
                }
                lfs_probe_log("SPLICE tag id=%u tempcount=%u tempbesttag=0x%08x", (unsigned)lfs_tag_id(tag), (unsigned)tempcount, (unsigned)tempbesttag);
            } else if (lfs_tag_type1(tag) == LFS_TYPE_TAIL) {
                tempsplit = (lfs_tag_chunk(tag) & 1);

                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag), &temptail, 8);
                if (err) {
                    lfs_probe_log("bd_read TAIL err=%d off=%lld", err, (long long)(off+sizeof(tag)));
                    if (err == LFS_ERR_CORRUPT) {
                        break;
                    }
                    lfs_probe_log("ERROR return from bd_read (tail): %d", err);
                    return err;
                }
                lfs_pair_fromle32(temptail);
                lfs_probe_log("TAIL tag temptail=[%u,%u] tempsplit=%d", (unsigned)temptail[0], (unsigned)temptail[1], (int)tempsplit);
            } else if (lfs_tag_type3(tag) == LFS_TYPE_FCRC) {
                err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], off+sizeof(tag),
                        &fcrc, sizeof(fcrc));
                if (err) {
                    lfs_probe_log("bd_read FCRC err=%d off=%lld", err, (long long)(off+sizeof(tag)));
                    if (err == LFS_ERR_CORRUPT) {
                        break;
                    }
                }

                lfs_fcrc_fromle32(&fcrc);
                hasfcrc = true;
                lfs_probe_log("FCRC found: size=%u crc=0x%08x", (unsigned)fcrc.size, (unsigned)fcrc.crc);
            }

            // found a match for our fetcher?
            if ((fmask & tag) == (fmask & ftag)) {
                struct lfs_diskoff doff = {dir->pair[0], off+sizeof(tag)};
                lfs_probe_log("MATCH test: fmask&tag=0x%08x fmask&ftag=0x%08x tag=0x%08x doff={%u,%lld}",
                        (unsigned)(fmask & tag), (unsigned)(fmask & ftag), (unsigned)tag, (unsigned)doff.block, (long long)doff.off);

                int res = cb(data, tag, &doff);
                lfs_probe_log("cb returned res=%d for tag=0x%08x", res, (unsigned)tag);
                if (res < 0) {
                    if (res == LFS_ERR_CORRUPT) {
                        lfs_probe_log("cb reported CORRUPT, breaking");
                        break;
                    }
                    lfs_probe_log("cb returned error %d, returning", res);
                    return res;
                }

                if (res == LFS_CMP_EQ) {
                    // found a match
                    tempbesttag = tag;
                    lfs_probe_log("cb CMP_EQ -> tempbesttag=0x%08x", (unsigned)tempbesttag);
                } else if ((LFS_MKTAG(0x7ff, 0x3ff, 0) & tag) ==
                        (LFS_MKTAG(0x7ff, 0x3ff, 0) & tempbesttag)) {
                    // found an identical tag, but contents didn't match
                    // this must mean that our besttag has been overwritten
                    tempbesttag = -1;
                    lfs_probe_log("identical tag contents mismatch -> tempbesttag invalidated");
                } else if (res == LFS_CMP_GT &&
                        lfs_tag_id(tag) <= lfs_tag_id(tempbesttag)) {
                    // found a greater match, keep track to keep things sorted
                    tempbesttag = tag | 0x80000000;
                    lfs_probe_log("cb CMP_GT -> tempbesttag=0x%08x", (unsigned)tempbesttag);
                }
            }
        }

        // found no valid commits?
        if (dir->off == 0) {
            // try the other block?
            lfs_probe_log("no valid commits in dir->pair[0]=%u, swapping pair and trying other block", (unsigned)dir->pair[0]);
            lfs_pair_swap(dir->pair);
            dir->rev = revs[(r+1)%2];
            continue;
        }

        // did we end on a valid commit? we may have an erased block
        dir->erased = false;
        if (maybeerased && dir->off % lfs->cfg->prog_size == 0) {
        #ifdef LFS_MULTIVERSION
            // note versions < lfs2.1 did not have fcrc tags, if
            // we're < lfs2.1 treat missing fcrc as erased data
            //
            // we don't strictly need to do this, but otherwise writing
            // to lfs2.0 disks becomes very inefficient
            if (lfs_fs_disk_version(lfs) < 0x00020001) {
                dir->erased = true;

            } else
        #endif
            if (hasfcrc) {
                // check for an fcrc matching the next prog's erased state, if
                // this failed most likely a previous prog was interrupted, we
                // need a new erase
                uint32_t fcrc_ = 0xffffffff;
                int err = lfs_bd_crc(lfs,
                        NULL, &lfs->rcache, lfs->cfg->block_size,
                        dir->pair[0], dir->off, fcrc.size, &fcrc_);
                if (err && err != LFS_ERR_CORRUPT) {
                    lfs_probe_log("bd_crc fcrc err=%d", err);
                    return err;
                }

                // found beginning of erased part?
                dir->erased = (fcrc_ == fcrc.crc);
                lfs_probe_log("fcrc check fcrc_=%u fcrc.crc=%u -> erased=%d", (unsigned)fcrc_, (unsigned)fcrc.crc, (int)dir->erased);
            }
        }

        // synthetic move
        if (lfs_gstate_hasmovehere(&lfs->gdisk, dir->pair)) {
            if (lfs_tag_id(lfs->gdisk.tag) == lfs_tag_id(besttag)) {
                besttag |= 0x80000000;
            } else if (besttag != -1 &&
                    lfs_tag_id(lfs->gdisk.tag) < lfs_tag_id(besttag)) {
                besttag -= LFS_MKTAG(0, 1, 0);
            }
            lfs_probe_log("gstate movehere: gdisk.tag=0x%08x besttag=0x%08x", (unsigned)lfs->gdisk.tag, (unsigned)besttag);
        }

        // found tag? or found best id?
        if (id) {
            *id = lfs_min(lfs_tag_id(besttag), dir->count);
            lfs_probe_log("set id=%u (besttag_id=%u dir->count=%u)", (unsigned)*id, (unsigned)lfs_tag_id(besttag), (unsigned)dir->count);
        }

        if (lfs_tag_isvalid(besttag)) {
            lfs_probe_log("RETURN besttag=0x%08x", (unsigned)besttag);
            return besttag;
        } else if (lfs_tag_id(besttag) < dir->count) {
            lfs_probe_log("RETURN LFS_ERR_NOENT (besttag_id=%u dir->count=%u)", (unsigned)lfs_tag_id(besttag), (unsigned)dir->count);
            return LFS_ERR_NOENT;
        } else {
            lfs_probe_log("RETURN 0 (no besttag)");
            return 0;
        }
    }

    lfs_probe_log("FINAL Corrupted dir pair at {0x%08x, 0x%08x}", (unsigned)dir->pair[0], (unsigned)dir->pair[1]);
    LFS_ERROR("Corrupted dir pair at {0x%"PRIx32", 0x%"PRIx32"}",
            dir->pair[0], dir->pair[1]);
    return LFS_ERR_CORRUPT;
}
