#include <stdio.h>

static int lfs_dir_find_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    struct lfs_dir_find_match *name = data;
    lfs_t *lfs = name->lfs;
    const struct lfs_diskoff *disk = buffer;

    /* prepare diff as in original code */
    lfs_size_t diff = lfs_min(name->size, lfs_tag_size(tag));

    /* Logging probe: record inputs before calling lfs_bd_cmp */
    {
        FILE *log = fopen("/tmp/logs/254.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_find_match ENTRY: name=%p, lfs=%p, buffer=%p\n",
                    (void *)name, (void *)lfs, buffer);
            fprintf(log, "  name->size=%llu, tag_size=%llu, diff=%llu\n",
                    (unsigned long long)name->size,
                    (unsigned long long)lfs_tag_size(tag),
                    (unsigned long long)diff);
            fprintf(log, "  disk->block=%llu, disk->off=%llu\n",
                    (unsigned long long)disk->block,
                    (unsigned long long)disk->off);
            fclose(log);
        }
    }

    // compare with disk
    int res = lfs_bd_cmp(lfs,
            NULL, &lfs->rcache, diff,
            disk->block, disk->off, name->name, diff);

    /* Logging probe: record result and state after lfs_bd_cmp call */
    {
        FILE *log = fopen("/tmp/logs/254.txt", "a");
        if (log) {
            fprintf(log, "lfs_bd_cmp RETURN: diff=%llu, res=%d\n",
                    (unsigned long long)diff, res);
            fprintf(log, "  post-call name->size=%llu, tag_size=%llu\n",
                    (unsigned long long)name->size,
                    (unsigned long long)lfs_tag_size(tag));
            if (res != LFS_CMP_EQ) {
                fprintf(log, "  comparison mismatch: res=%d\n", res);
            } else {
                fprintf(log, "  comparison equal\n");
            }
            fclose(log);
        }
    }

    if (res != LFS_CMP_EQ) {
        return res;
    }

    // only equal if our size is still the same
    if (name->size != lfs_tag_size(tag)) {
        FILE *log = fopen("/tmp/logs/254.txt", "a");
        if (log) {
            fprintf(log, "size mismatch after compare: name->size=%llu, tag_size=%llu\n",
                (unsigned long long)name->size, (unsigned long long)lfs_tag_size(tag));
            fclose(log);
        }
        return (name->size < lfs_tag_size(tag)) ? LFS_CMP_LT : LFS_CMP_GT;
    }

    /* found a match! */
    {
        FILE *log = fopen("/tmp/logs/254.txt", "a");
        if (log) {
            fprintf(log, "found a match\n");
            fclose(log);
        }
    }

    return LFS_CMP_EQ;
}
