#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

static void lfs_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) { return; }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    lfs_log("ENTER lfs_dir_relocatingcommit: lfs=%p dir=%p pair=[%llu,%llu] attrcount=%d pdir=%p",
            (void*)lfs, (void*)dir, (unsigned long long)pair[0], (unsigned long long)pair[1],
            attrcount, (void*)pdir);

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
            lfs_log("ATTR[%d]: CREATE id=%u new_count=%d", i, (unsigned)lfs_tag_id(attrs[i].tag), dir->count);
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
            lfs_log("ATTR[%d]: DELETE id=%u new_count=%d", i, (unsigned)lfs_tag_id(attrs[i].tag), dir->count);
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
            lfs_log("ATTR[%d]: TAIL tail=[%llu,%llu] split=%d", i,
                    (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1], (int)dir->split);
        }
    }

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        lfs_log("hasdelete && dir->count==0: lfs_fs_pred returned %d pdir->split=%d", err, (int)pdir->split);
        if (err && err != LFS_ERR_NOENT) {
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            lfs_log("Dropping directory: state set to LFS_OK_DROPPED");
            goto fixmlist;
        }
    }

    if (dir->erased) {
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        // traverse attrs that need to be written out
        lfs_pair_tole32(dir->tail);
        lfs_log("Before lfs_dir_traverse: dir->tail=[%llu,%llu] cb=%p", (unsigned long long)dir->tail[0],
                (unsigned long long)dir->tail[1], (void*)lfs_dir_commit_commit);
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});
        lfs_pair_fromle32(dir->tail);
        lfs_log("After lfs_dir_traverse: err=%d dir->tail=[%llu,%llu]", err,
                (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1]);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_log("lfs_dir_traverse returned NOSPC or CORRUPT -> goto compact");
                goto compact;
            }
            lfs_log("lfs_dir_traverse returned error %d -> return", err);
            return err;
        }

        // commit any global diffs if we have any
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            err = lfs_dir_getgstate(lfs, dir, &delta);
            lfs_log("Global diff present: lfs_dir_getgstate returned %d", err);
            if (err) {
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            lfs_log("lfs_dir_commitattr returned %d", err);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    lfs_log("lfs_dir_commitattr NOSPC or CORRUPT -> goto compact");
                    goto compact;
                }
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        lfs_log("lfs_dir_commitcrc returned %d", err);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_log("lfs_dir_commitcrc NOSPC or CORRUPT -> goto compact");
                goto compact;
            }
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        lfs_log("Commit successful: dir->off=%llu dir->etag=%u", (unsigned long long)dir->off, (unsigned)dir->etag);

        goto fixmlist;
    }

compact:
    // fall back to compaction
    lfs_cache_drop(lfs, &lfs->pcache);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    lfs_log("lfs_dir_splittingcompact returned %d", state);
    if (state < 0) {
        return state;
    }

    goto fixmlist;

fixmlist:;
    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    lfs_log("Entering fixmlist: state=%d oldpair=[%llu,%llu]", state,
            (unsigned long long)oldpair[0], (unsigned long long)oldpair[1]);
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        lfs_log("mlist entry: d=%p id=%d type=%d m.pair=[%llu,%llu] m.count=%d m.split=%d",
                (void*)d, d->id, d->type,
                (unsigned long long)d->m.pair[0], (unsigned long long)d->m.pair[1],
                d->m.count, (int)d->m.split);
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            lfs_log("mlist match found: d=%p -> updating m from dir=%p", (void*)d, (void*)dir);
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                        lfs_log("mlist d=%p: DELETE matched id=%u -> cleared pair", (void*)d, (unsigned)lfs_tag_id(attrs[i].tag));
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                        lfs_log("mlist d=%p: DELETE adjust id now=%d", (void*)d, d->id);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                        lfs_log("mlist d=%p: CREATE adjust id now=%d", (void*)d, d->id);
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                lfs_log("mlist d=%p: id on tail now=%d fetching tail=%p", (void*)d, d->id, (void*)d->m.tail);
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                lfs_log("lfs_dir_fetch returned %d for d=%p", err, (void*)d);
                if (err) {
                    return err;
                }
            }
        }
    }

    lfs_log("EXIT lfs_dir_relocatingcommit: returning state=%d", state);
    return state;
}
