#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>

/* Probe helper: append human-readable logs for control flow and state. */
static void lfs_probe_log(const char *event, const void *attrs, int attrcount,
        const void *pair, const void *dir_void, int state_or_err) {
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }

    fprintf(f, "EVENT=%s attrs=%p attrcount=%d pair=%p dir=%p state/err=%d\n",
            event, attrs, attrcount, pair, dir_void, state_or_err);

    /* Flush to make logs available even if program crashes soon after. */
    fflush(f);
    fclose(f);
}

static void lfs_probe_log_attrs(const char *event, const struct lfs_mattr *attrs, int attrcount) {
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }

    fprintf(f, "EVENT=%s attrs=%p attrcount=%d\n", event, (const void*)attrs, attrcount);
    if (attrs) {
        int tolog = attrcount < 8 ? attrcount : 8;
        for (int i = 0; i < tolog; i++) {
            fprintf(f, "  attrs[%d].tag=0x%08" PRIx32 " buffer=%p\n",
                    i, (uint32_t)attrs[i].tag, (const void*)attrs[i].buffer);
        }
        if (attrcount > tolog) {
            fprintf(f, "  ... (%d more attrs)\n", attrcount - tolog);
        }
    }

    fflush(f);
    fclose(f);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    /* Log entry and key state. */
    lfs_probe_log("enter_relocatingcommit", attrs, attrcount, pair, dir, 0);
    lfs_probe_log_attrs("initial_attrs", attrs, attrcount);

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
        }
    }

    /* Log after computing directory changes. */
    lfs_probe_log("after_compute_changes", attrs, attrcount, pair, dir, dir->count);

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        if (err && err != LFS_ERR_NOENT) {
            lfs_probe_log("pred_failed", attrs, attrcount, pair, dir, err);
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            lfs_probe_log("dropped_by_pred", attrs, attrcount, pair, dir, state);
            goto fixmlist;
        }
    }

    if (dir->erased) {
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        /* Log just before calling lfs_dir_traverse so we can detect
         * any modification to the attrs pointer by that call. */
        lfs_probe_log("before_lfs_dir_traverse", attrs, attrcount, pair, dir, 0);
        lfs_probe_log_attrs("before_traverse_attrs", attrs, attrcount);

        // traverse attrs that need to be written out
        lfs_pair_tole32(dir->tail);
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});
        lfs_pair_fromle32(dir->tail);

        /* Log immediately after traverse including returned err and
         * the attrs pointer value (to capture if it was changed). */
        lfs_probe_log("after_lfs_dir_traverse", attrs, attrcount, pair, dir, err);
        lfs_probe_log_attrs("after_traverse_attrs", attrs, attrcount);

        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                goto compact;
            }
            return err;
        }

        // commit any global diffs if we have any
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            err = lfs_dir_getgstate(lfs, dir, &delta);
            if (err) {
                lfs_probe_log("getgstate_failed", attrs, attrcount, pair, dir, err);
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    goto compact;
                }
                lfs_probe_log("commitattr_failed", attrs, attrcount, pair, dir, err);
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                goto compact;
            }
            lfs_probe_log("commitcrc_failed", attrs, attrcount, pair, dir, err);
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        lfs_probe_log("commit_successful", attrs, attrcount, pair, dir, 0);
        goto fixmlist;
    }

compact:
    // fall back to compaction
    lfs_cache_drop(lfs, &lfs->pcache);

    /* Log before compaction to capture attrs pointer and contents. */
    lfs_probe_log("before_splittingcompact", attrs, attrcount, pair, dir, 0);
    lfs_probe_log_attrs("before_compact_attrs", attrs, attrcount);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);

    /* Log after compaction call to see whether attrs pointer was affected. */
    lfs_probe_log("after_splittingcompact", attrs, attrcount, pair, dir, state);
    lfs_probe_log_attrs("after_compact_attrs", attrs, attrcount);

    if (state < 0) {
        return state;
    }

    goto fixmlist;

fixmlist:;
    /* Log entry to fixmlist so we can associate subsequent changes with this
     * relocation commit. */
    lfs_probe_log("enter_fixmlist", attrs, attrcount, pair, dir, state);

    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            d->m = *dir;
            lfs_probe_log("mlist_match_before_attrsloop", attrs, attrcount, pair, d, d->id);
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                    }
                }
            }

            lfs_probe_log("mlist_match_after_attrsloop", attrs, attrcount, pair, d, d->id);

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                if (err) {
                    lfs_probe_log("dir_fetch_failed_in_fixmlist", attrs, attrcount, pair, d, err);
                    return err;
                }
            }
        }
    }

    lfs_probe_log("exit_relocatingcommit", attrs, attrcount, pair, dir, state);
    return state;
}
