#include <stdio.h>
#include <stdarg.h>

/* Helper logging function: appends formatted logs to /tmp/logs/462.txt */
static void lfs_debug_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/462.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int lfs_file_close(lfs_t *lfs, lfs_file_t *file) {
    lfs_debug_log("ENTER lfs_file_close: lfs=%p file=%p", (void*)lfs, (void*)file);

    int err = LFS_LOCK(lfs->cfg);
    lfs_debug_log("LFS_LOCK returned %d", err);
    if (err) {
        lfs_debug_log("LFS_LOCK failed -> returning %d", err);
        return err;
    }

    LFS_TRACE("lfs_file_close(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_debug_log("Calling lfs_file_close_ with lfs=%p file=%p", (void*)lfs, (void*)file);
    err = lfs_file_close_(lfs, file);
    lfs_debug_log("lfs_file_close_ returned %d", err);

    LFS_TRACE("lfs_file_close -> %d", err);

    /* Probe around the unlock to capture evidence of its execution
       Even if LFS_UNLOCK expands to a no-op, these logs show the control
       flow reaching the unlock site. */
    lfs_debug_log("About to call LFS_UNLOCK(lfs->cfg) [lfs=%p]", (void*)lfs);
    LFS_UNLOCK(lfs->cfg);
    lfs_debug_log("Returned from LFS_UNLOCK(lfs->cfg) [lfs=%p]", (void*)lfs);

    lfs_debug_log("EXIT lfs_file_close -> %d", err);
    return err;
}
