static lfs_ssize_t lfs_file_flushedwrite(lfs_t *lfs, lfs_file_t *file,
        const void *buffer, lfs_size_t size) {
    const uint8_t *data = buffer;
    lfs_size_t nsize = size;

    if ((file->flags & LFS_F_INLINE) &&
            lfs_max(file->pos+nsize, file->ctz.size) > lfs->inline_max) {
        // inline file doesn't fit anymore
        int err = lfs_file_outline(lfs, file);
        if (err) {
            file->flags |= LFS_F_ERRED;
            return err;
        }
    }

    while (nsize > 0) {
        // check if we need a new block
        if (!(file->flags & LFS_F_WRITING) ||
                file->off == lfs->cfg->block_size) {
            if (!(file->flags & LFS_F_INLINE)) {
                if (!(file->flags & LFS_F_WRITING) && file->pos > 0) {
                    // find out which block we're extending from
                    /*
                     * Probe/Logging inserted here to capture the exact
                     * arguments and pointer passed to lfs_ctz_find.
                     * We intentionally create the same compound literal
                     * as the original code did, but bind it to a local
                     * pointer so we can log its address and value before
                     * and after the call. This preserves original logic
                     * while providing evidence of writes into the
                     * temporary (which constitute dead code per MISRA 2.2
                     * if the caller does not use the output).
                     */
                    lfs_off_t *probe_off = &(lfs_off_t){0};
                    FILE *lfs_log = fopen("/tmp/logs/502.txt", "a");
                    if (lfs_log) {
                        fprintf(lfs_log,
                            "lfs_file_flushedwrite: calling lfs_ctz_find -- "
                            "pos=%llu, ctz.size=%llu, ctz.head=%llu, "
                            "file->block=%llu, probe_off=%p\n",
                            (unsigned long long)file->pos,
                            (unsigned long long)file->ctz.size,
                            (unsigned long long)file->ctz.head,
                            (unsigned long long)file->block,
                            (void *)probe_off);
                        fclose(lfs_log);
                    }

                    int err = lfs_ctz_find(lfs, NULL, &file->cache,
                            file->ctz.head, file->ctz.size,
                            file->pos-1, &file->block, probe_off);

                    /* log result and the written value (if any) to the
                     * temporary probe_off so that downstream analysis can
                     * observe that the called function wrote into a
                     * temporary whose value is not used by the caller. */
                    lfs_log = fopen("/tmp/logs/502.txt", "a");
                    if (lfs_log) {
                        fprintf(lfs_log,
                            "lfs_file_flushedwrite: lfs_ctz_find returned err=%d, *probe_off=%llu\n",
                            err, (unsigned long long)*probe_off);
                        fclose(lfs_log);
                    }

                    if (err) {
                        file->flags |= LFS_F_ERRED;
                        return err;
                    }

                    // mark cache as dirty since we may have read data into it
                    lfs_cache_zero(lfs, &file->cache);
                }

                // extend file with new blocks
                lfs_alloc_ckpoint(lfs);
                int err = lfs_ctz_extend(lfs, &file->cache, &lfs->rcache,
                        file->block, file->pos,
                        &file->block, &file->off);
                if (err) {
                    file->flags |= LFS_F_ERRED;
                    return err;
                }
            } else {
                file->block = LFS_BLOCK_INLINE;
                file->off = file->pos;
            }

            file->flags |= LFS_F_WRITING;
        }

        // program as much as we can in current block
        lfs_size_t diff = lfs_min(nsize, lfs->cfg->block_size - file->off);
        while (true) {
            int err = lfs_bd_prog(lfs, &file->cache, &lfs->rcache, true,
                    file->block, file->off, data, diff);
            if (err) {
                if (err == LFS_ERR_CORRUPT) {
                    goto relocate;
                }
                file->flags |= LFS_F_ERRED;
                return err;
            }

            break;
relocate:
            err = lfs_file_relocate(lfs, file);
            if (err) {
                file->flags |= LFS_F_ERRED;
                return err;
            }
        }

        file->pos += diff;
        file->off += diff;
        data += diff;
        nsize -= diff;

        lfs_alloc_ckpoint(lfs);
    }

    return size;
}
