#include <stdio.h>
#include <stdarg.h>

/* Probe logger: writes to /tmp/logs/525.txt */
static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/525.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    /* Log entry and relevant pointers to capture potential macro-argument confusion */
    lfs_log_probe("ENTER lfs_file_opencfg: lfs=%p, file=%p, path=%s, flags=0x%X, cfg_param=%p, lfs->cfg=%p",
            (void*)lfs, (void*)file, (path?path:"(null)"), (unsigned)flags, (void*)cfg, (void*)(lfs ? lfs->cfg : (void*)0));

    int err = LFS_LOCK(lfs->cfg);

    /* Log result of LFS_LOCK and surrounding state. This helps demonstrate if the lock macro
       is invariant (always returning 0) and whether it used the intended argument. */
    lfs_log_probe("AFTER LFS_LOCK: err=%d, cfg_param=%p, lfs->cfg=%p",
            err, (void*)cfg, (void*)(lfs ? lfs->cfg : (void*)0));

    if (err) {
        lfs_log_probe("LFS_LOCK returned non-zero (%d); returning from lfs_file_opencfg", err);
        return err;
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    lfs_log_probe("lfs_file_opencfg_ returned %d", err);

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    lfs_log_probe("About to unlock lfs->cfg=%p and return err=%d", (void*)(lfs ? lfs->cfg : (void*)0), err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
