#include <stdio.h>
#include <stdarg.h>

/* Probe logging helpers: append human-readable traces to /tmp/logs/537.txt */
static void probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/537.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static void probe_log_path_state(const char *stage, const char * const *pathptr) {
    const char *p = NULL;
    if (pathptr) {
        p = *pathptr;
    }
    probe_log("%s: path_var_addr=%p, pointer_value=%p, string=%s\n",
            stage, (void *)pathptr, (void *)p, (p ? p : "(null)"));
}

static int lfs_file_opencfg_(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
#ifndef LFS_READONLY
    // deorphan if we haven't yet, needed at most once after poweron
    if ((flags & LFS_O_WRONLY) == LFS_O_WRONLY) {
        int err = lfs_fs_forceconsistency(lfs);
        if (err) {
            /* log and return */
            probe_log("lfs_file_opencfg_: early return from lfs_fs_forceconsistency err=%d path_addr=%p path=%s\n",
                    err, (void *)&path, (path ? path : "(null)"));
            return err;
        }
    }
#else
    LFS_ASSERT((flags & LFS_O_RDONLY) == LFS_O_RDONLY);
#endif

    /* initial log of entry state */
    probe_log("lfs_file_opencfg_: ENTRY flags=0x%X file=%p path_addr=%p path=%s cfg=%p\n",
            flags, (void *)file, (void *)&path, (path ? path : "(null)"), (void *)cfg);
    probe_log_path_state("before_dir_find", &path);

    // setup simple file details
    int err;
    file->cfg = cfg;
    file->flags = flags;
    file->pos = 0;
    file->off = 0;
    file->cache.buffer = NULL;

    // allocate entry for file if it doesn't exist
    lfs_stag_t tag = lfs_dir_find(lfs, &file->m, &path, &file->id);

    /* log after lfs_dir_find to capture if the pointer itself was changed */
    probe_log_path_state("after_dir_find", &path);
    probe_log("lfs_dir_find returned tag=%d for file=%p id=%d\n",
            (int)tag, (void *)file, (int)file->id);

    if (tag < 0 && !(tag == LFS_ERR_NOENT && lfs_path_islast(path))) {
        err = tag;
        probe_log("lfs_file_opencfg_: aborting after dir_find tag=%d path=%s err=%d\n",
                (int)tag, (path ? path : "(null)"), err);
        goto cleanup;
    }

    // get id, add to list of mdirs to catch update changes
    file->type = LFS_TYPE_REG;
    lfs_mlist_append(lfs, (struct lfs_mlist *)file);

#ifdef LFS_READONLY
    if (tag == LFS_ERR_NOENT) {
        err = LFS_ERR_NOENT;
        probe_log("lfs_file_opencfg_: read-only and noent path=%s\n", (path ? path : "(null)"));
        goto cleanup;
#else
    if (tag == LFS_ERR_NOENT) {
        if (!(flags & LFS_O_CREAT)) {
            err = LFS_ERR_NOENT;
            probe_log("lfs_file_opencfg_: no create flag and noent path=%s\n", (path ? path : "(null)"));
            goto cleanup;
        }

        // don't allow trailing slashes
        if (lfs_path_isdir(path)) {
            err = LFS_ERR_NOTDIR;
            probe_log("lfs_file_opencfg_: path is a dir (trailing slash) path=%s\n", (path ? path : "(null)"));
            goto cleanup;
        }

        // check that name fits
        lfs_size_t nlen = lfs_path_namelen(path);
        if (nlen > lfs->name_max) {
            err = LFS_ERR_NAMETOOLONG;
            probe_log("lfs_file_opencfg_: name too long nlen=%u name_max=%u path=%s\n",
                    (unsigned)nlen, (unsigned)lfs->name_max, (path ? path : "(null)"));
            goto cleanup;
        }

        // get next slot and create entry to remember name
        err = lfs_dir_commit(lfs, &file->m, LFS_MKATTRS(
                {LFS_MKTAG(LFS_TYPE_CREATE, file->id, 0), NULL},
                {LFS_MKTAG(LFS_TYPE_REG, file->id, nlen), path},
                {LFS_MKTAG(LFS_TYPE_INLINESTRUCT, file->id, 0), NULL}));

        // it may happen that the file name doesn't fit in the metadata blocks, e.g., a 256 byte file name will
        // not fit in a 128 byte block.
        err = (err == LFS_ERR_NOSPC) ? LFS_ERR_NAMETOOLONG : err;
        if (err) {
            probe_log("lfs_file_opencfg_: lfs_dir_commit failed err=%d path=%s\n", err, (path ? path : "(null)"));
            goto cleanup;
        }

        tag = LFS_MKTAG(LFS_TYPE_INLINESTRUCT, 0, 0);
    } else if (flags & LFS_O_EXCL) {
        err = LFS_ERR_EXIST;
        probe_log("lfs_file_opencfg_: exclusive open and exists path=%s\n", (path ? path : "(null)"));
        goto cleanup;
#endif
    } else if (lfs_tag_type3(tag) != LFS_TYPE_REG) {
        err = LFS_ERR_ISDIR;
        probe_log("lfs_file_opencfg_: found non-regular type path=%s tag=%d\n", (path ? path : "(null)"), (int)tag);
        goto cleanup;
#ifndef LFS_READONLY
    } else if (flags & LFS_O_TRUNC) {
        // truncate if requested
        tag = LFS_MKTAG(LFS_TYPE_INLINESTRUCT, file->id, 0);
        file->flags |= LFS_F_DIRTY;
        probe_log("lfs_file_opencfg_: truncating file id=%d path=%s\n", (int)file->id, (path ? path : "(null)"));
#endif
    } else {
        // try to load what's on disk, if it's inlined we'll fix it later
        tag = lfs_dir_get(lfs, &file->m, LFS_MKTAG(0x700, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_STRUCT, file->id, 8), &file->ctz);
        if (tag < 0) {
            err = tag;
            probe_log("lfs_file_opencfg_: lfs_dir_get failed tag=%d path=%s\n", (int)tag, (path ? path : "(null)"));
            goto cleanup;
        }
        lfs_ctz_fromle32(&file->ctz);
    }

    // fetch attrs
    for (unsigned i = 0; i < file->cfg->attr_count; i++) {
        // if opened for read / read-write operations
        if ((file->flags & LFS_O_RDONLY) == LFS_O_RDONLY) {
            lfs_stag_t res = lfs_dir_get(lfs, &file->m,
                    LFS_MKTAG(0x7ff, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_USERATTR + file->cfg->attrs[i].type,
                        file->id, file->cfg->attrs[i].size),
                        file->cfg->attrs[i].buffer);
            if (res < 0 && res != LFS_ERR_NOENT) {
                err = res;
                probe_log("lfs_file_opencfg_: attr fetch failed res=%d i=%u path=%s\n",
                        (int)res, (unsigned)i, (path ? path : "(null)"));
                goto cleanup;
            }
        }

#ifndef LFS_READONLY
        // if opened for write / read-write operations
        if ((file->flags & LFS_O_WRONLY) == LFS_O_WRONLY) {
            if (file->cfg->attrs[i].size > lfs->attr_max) {
                err = LFS_ERR_NOSPC;
                probe_log("lfs_file_opencfg_: attr too large i=%u size=%u attr_max=%u path=%s\n",
                        (unsigned)i, (unsigned)file->cfg->attrs[i].size, (unsigned)lfs->attr_max, (path ? path : "(null)"));
                goto cleanup;
            }

            file->flags |= LFS_F_DIRTY;
        }
#endif
    }

    // allocate buffer if needed
    if (file->cfg->buffer) {
        file->cache.buffer = file->cfg->buffer;
    } else {
        file->cache.buffer = lfs_malloc(lfs->cfg->cache_size);
        if (!file->cache.buffer) {
            err = LFS_ERR_NOMEM;
            probe_log("lfs_file_opencfg_: failed to allocate cache buffer size=%u path=%s\n",
                    (unsigned)lfs->cfg->cache_size, (path ? path : "(null)"));
            goto cleanup;
        }
    }

    // zero to avoid information leak
    lfs_cache_zero(lfs, &file->cache);

    if (lfs_tag_type3(tag) == LFS_TYPE_INLINESTRUCT) {
        // load inline files
        file->ctz.head = LFS_BLOCK_INLINE;
        file->ctz.size = lfs_tag_size(tag);
        file->flags |= LFS_F_INLINE;
        file->cache.block = file->ctz.head;
        file->cache.off = 0;
        file->cache.size = lfs->cfg->cache_size;

        // don't always read (may be new/trunc file)
        if (file->ctz.size > 0) {
            lfs_stag_t res = lfs_dir_get(lfs, &file->m,
                    LFS_MKTAG(0x700, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_STRUCT, file->id,
                        lfs_min(file->cache.size, 0x3fe)),
                    file->cache.buffer);
            if (res < 0) {
                err = res;
                probe_log("lfs_file_opencfg_: inline read failed res=%d path=%s\n", (int)res, (path ? path : "(null)"));
                goto cleanup;
            }
        }
    }

    probe_log("lfs_file_opencfg_: SUCCESS id=%d flags=0x%X path=%s\n",
            (int)file->id, (int)file->flags, (path ? path : "(null)"));
    return 0;

cleanup:
    // clean up lingering resources
#ifndef LFS_READONLY
    file->flags |= LFS_F_ERRED;
#endif
    probe_log("lfs_file_opencfg_: CLEANUP err=%d file=%p flags=0x%X id=%d path=%s\n",
            err, (void *)file, (int)file->flags, (int)file->id, (path ? path : "(null)"));
    lfs_file_close_(lfs, file);
    return err;
}
