#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/592.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    /* Log entry with parameters (do not dereference lfs->cfg here) */
    probe_log("ENTER lfs_file_seek: lfs=%p file=%p off=%" PRId32 " whence=%d",
            (void*)lfs, (void*)file, off, whence);

    int err = LFS_LOCK(lfs->cfg);
    probe_log("LFS_LOCK returned err=%d for lfs->cfg=%p", err, (void*)lfs->cfg);
    if (err) {
        probe_log("RETURN early from lfs_file_seek due to lock error: %d", err);
        return err;
    }
    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    probe_log("lfs_file_seek_: returned res=%" PRId32, res);
    LFS_TRACE("lfs_file_seek -> %"PRId32, res);

    /* Capture cfg pointer before and after unlock to provide evidence that
       LFS_UNLOCK(lfs->cfg) performs no observable action. */
    void *cfg_before = (void*)lfs->cfg;
    probe_log("About to call LFS_UNLOCK with cfg=%p", cfg_before);

    LFS_UNLOCK(lfs->cfg);

    void *cfg_after = (void*)lfs->cfg;
    probe_log("After LFS_UNLOCK, cfg_before=%p cfg_after=%p (no observable change expected if no-op)",
            cfg_before, cfg_after);

    probe_log("EXIT lfs_file_seek returning res=%" PRId32, res);
    return res;
}
