#include <stdio.h>
#include <inttypes.h>

/* Probe functions to capture control flow and state for lfs_file_seek.
   These write logs to /tmp/logs/592.txt so the empty LFS_TRACE macro
   case (no-op) can be observed and reproduced. */
static void lfs_probe_log_enter(lfs_t *lfs, lfs_file_t *file, lfs_soff_t off, int whence) {
    FILE *f = fopen("/tmp/logs/592.txt", "a");
    if (f) {
        fprintf(f, "ENTER lfs_file_seek: lfs=%p file=%p off=%" PRId32 " whence=%d\n",
                (void*)lfs, (void*)file, off, whence);
        fclose(f);
    }
}

static void lfs_probe_log_exit(lfs_soff_t res) {
    FILE *f = fopen("/tmp/logs/592.txt", "a");
    if (f) {
        fprintf(f, "EXIT  lfs_file_seek -> %" PRId32 "\n", res);
        fclose(f);
    }
}

lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }
    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    /* Additional probe to log entry state even if LFS_TRACE is a no-op */
    lfs_probe_log_enter(lfs, file, off, whence);

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    LFS_TRACE("lfs_file_seek -> %"PRId32, res);
    /* Additional probe to log exit state (result) */
    lfs_probe_log_exit(res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
