#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append formatted messages to /tmp/logs/701.txt */
static void lfs_probe_logf(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/701.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static void lfs_probe_log_lock(const struct lfs_config *cfg, int err) {
    lfs_probe_logf("lfs_format: LFS_LOCK(cfg) -> %d, cfg=%p", err, (void*)cfg);
}

int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);
    /* Probe: log result of LFS_LOCK to capture whether the following if is reachable */
    lfs_probe_log_lock(cfg, err);

    if (err) {
        /* Probe: log entering the early-return branch (should be unreachable if LFS_LOCK returns 0) */
        lfs_probe_logf("lfs_format: if(err) taken, returning err=%d", err);
        return err;
    }
    lfs_probe_logf("lfs_format: if(err) not taken (err=%d), continuing", err);

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    lfs_probe_logf("lfs_format: calling lfs_format_(lfs=%p, cfg=%p)", (void*)lfs, (void*)cfg);
    err = lfs_format_(lfs, cfg);
    lfs_probe_logf("lfs_format: lfs_format_ returned %d", err);

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);
    lfs_probe_logf("lfs_format: unlocked cfg=%p, returning %d", (void*)cfg, err);
    return err;
}
