/* Probe logging helper. Appends messages to /tmp/logs/715.txt. */
static void lfs_log_probe(const char *fmt, ...){
    FILE *f = fopen("/tmp/logs/715.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

