#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>

/* Probe logging helper - appends messages to /tmp/logs/736.txt */
static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/736.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_fs_deorphan(lfs_t *lfs, bool powerloss) {
    lfs_log_probe("lfs_fs_deorphan: entry powerloss=%d, hasorphans=%d",
            (int)powerloss, (int)lfs_gstate_hasorphans(&lfs->gstate));

    if (!lfs_gstate_hasorphans(&lfs->gstate)) {
        lfs_log_probe("lfs_fs_deorphan: no orphans -> return 0");
        return 0;
    }

    /*
     * Check for orphans in two separate passes:
     * - 1 for half-orphans (relocations)
     * - 2 for full-orphans (removes/renames)
     */
    int pass = 0;
    while (pass < 2) {
        lfs_log_probe("lfs_fs_deorphan: loop start pass=%d", pass);

        /* Fix any orphans */
        lfs_mdir_t pdir = {.split = true, .tail = {0, 1}};
        lfs_mdir_t dir;
        bool moreorphans = false;

        /* iterate over all directory directory entries */
        while (!lfs_pair_isnull(pdir.tail)) {
            lfs_log_probe("lfs_fs_deorphan: fetching dir for pdir.tail={0x%016" PRIx32 ",0x%016" PRIx32 "}", (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);

            int err = lfs_dir_fetch(lfs, &dir, pdir.tail);
            lfs_log_probe("lfs_fs_deorphan: lfs_dir_fetch returned %d for tail={0x%08" PRIx32 ",0x%08" PRIx32 "}", err, (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);
            if (err) {
                lfs_log_probe("lfs_fs_deorphan: lfs_dir_fetch error -> return %d", err);
                return err;
            }

            /* check head blocks for orphans */
            if (!pdir.split) {
                /* check if we have a parent */
                lfs_mdir_t parent;
                lfs_stag_t tag = lfs_fs_parent(lfs, pdir.tail, &parent);
                lfs_log_probe("lfs_fs_deorphan: lfs_fs_parent returned tag=%d for tail={0x%08" PRIx32 ",0x%08" PRIx32 "}", (int)tag, (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);
                if (tag < 0 && tag != LFS_ERR_NOENT) {
                    lfs_log_probe("lfs_fs_deorphan: lfs_fs_parent error tag=%d -> return %d", (int)tag, (int)tag);
                    return tag;
                }

                if (pass == 0 && tag != LFS_ERR_NOENT) {
                    lfs_block_t pair[2];
                    lfs_stag_t state = lfs_dir_get(lfs, &parent,
                            LFS_MKTAG(0x7ff, 0x3ff, 0), tag, pair);
                    lfs_log_probe("lfs_fs_deorphan: lfs_dir_get returned state=%d for parent (tag=%d)", (int)state, (int)tag);
                    if (state < 0) {
                        lfs_log_probe("lfs_fs_deorphan: lfs_dir_get error -> return %d", (int)state);
                        return state;
                    }
                    lfs_pair_fromle32(pair);

                    lfs_log_probe("lfs_fs_deorphan: comparing pair={0x%08" PRIx32 ",0x%08" PRIx32 "} with pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "}",
                            (uint32_t)pair[0], (uint32_t)pair[1], (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);

                    if (!lfs_pair_issync(pair, pdir.tail)) {
                        /* we have desynced */
                        lfs_log_probe("lfs_fs_deorphan: desynced detected for pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "}", (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);

                        /* fix pending move in this pair? this looks like an
                         * optimization but is in fact _required_ since
                         * relocating may outdate the move.
                         */
                        uint16_t moveid = 0x3ff;
                        if (lfs_gstate_hasmovehere(&lfs->gstate, pdir.pair)) {
                            moveid = lfs_tag_id(lfs->gstate.tag);
                            lfs_log_probe("lfs_fs_deorphan: move here detected pdir.pair={0x%08" PRIx32 ",0x%08" PRIx32 "}, moveid=0x%03x",
                                    (uint32_t)pdir.pair[0], (uint32_t)pdir.pair[1], moveid);
                            lfs_fs_prepmove(lfs, 0x3ff, NULL);
                            lfs_log_probe("lfs_fs_deorphan: lfs_fs_prepmove called (moveid placeholder)");
                        }

                        lfs_pair_tole32(pair);
                        state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                                {LFS_MKTAG_IF(moveid != 0x3ff,
                                    LFS_TYPE_DELETE, moveid, 0), NULL},
                                {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8),
                                    pair}));
                        lfs_pair_fromle32(pair);
                        lfs_log_probe("lfs_fs_deorphan: lfs_dir_orphaningcommit returned state=%d", (int)state);
                        if (state < 0) {
                            lfs_log_probe("lfs_fs_deorphan: lfs_dir_orphaningcommit error -> return %d", (int)state);
                            return state;
                        }

                        /* did our commit create more orphans? */
                        if (state == LFS_OK_ORPHANED) {
                            moreorphans = true;
                            lfs_log_probe("lfs_fs_deorphan: commit created more orphans (state==LFS_OK_ORPHANED)");
                        }

                        /* refetch tail */
                        continue;
                    }
                }

                /* note we only check for full orphans if we may have had a
                 * power-loss, otherwise orphans are created intentionally
                 * during operations such as lfs_mkdir
                 */
                if (pass == 1 && tag == LFS_ERR_NOENT && powerloss) {
                    /* we are an orphan */
                    lfs_log_probe("lfs_fs_deorphan: full orphan detected for pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "}", (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);

                    /* steal state */
                    err = lfs_dir_getgstate(lfs, &dir, &lfs->gdelta);
                    lfs_log_probe("lfs_fs_deorphan: lfs_dir_getgstate returned %d", err);
                    if (err) {
                        lfs_log_probe("lfs_fs_deorphan: lfs_dir_getgstate error -> return %d", err);
                        return err;
                    }

                    /* steal tail */
                    lfs_pair_tole32(dir.tail);
                    int state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                            {LFS_MKTAG(LFS_TYPE_TAIL + dir.split, 0x3ff, 8),
                                dir.tail}));
                    lfs_pair_fromle32(dir.tail);
                    lfs_log_probe("lfs_fs_deorphan: lfs_dir_orphaningcommit (full orphan) returned state=%d", state);
                    if (state < 0) {
                        lfs_log_probe("lfs_fs_deorphan: lfs_dir_orphaningcommit error -> return %d", state);
                        return state;
                    }

                    /* did our commit create more orphans? */
                    if (state == LFS_OK_ORPHANED) {
                        moreorphans = true;
                        lfs_log_probe("lfs_fs_deorphan: full orphan commit created more orphans (state==LFS_OK_ORPHANED)");
                    }

                    /* refetch tail */
                    continue;
                }
            }

            pdir = dir;
            lfs_log_probe("lfs_fs_deorphan: advanced pdir to next dir (tail now={0x%08" PRIx32 ",0x%08" PRIx32 "})",
                    (uint32_t)pdir.tail[0], (uint32_t)pdir.tail[1]);
        }

        lfs_log_probe("lfs_fs_deorphan: pass=%d finished, moreorphans=%d", pass, (int)moreorphans);
        pass = moreorphans ? 0 : pass+1;
    }

    /* mark orphans as fixed */
    int ret = lfs_fs_preporphans(lfs, -lfs_gstate_getorphans(&lfs->gstate));
    lfs_log_probe("lfs_fs_deorphan: exiting, lfs_fs_preporphans returned %d", ret);
    return ret;
}
