#include <stdio.h>
#include <stddef.h>

/* Probe logging helper: append simple records to /tmp/logs/812.txt
   Avoids changing program logic; used only to record control flow and state. */
static void lfs_probe_log(const char *stage, const void *lfs_ptr, int err) {
    FILE *f = fopen("/tmp/logs/812.txt", "a");
    if (!f) {
        return;
    }
    /* Log stage, pointer value and error code. */
    fprintf(f, "%s: lfs=%p err=%d\n", stage, lfs_ptr, err);
    fclose(f);
}

int lfs_fs_mkconsistent(lfs_t *lfs) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: record result of lock call and pointer. */
    lfs_probe_log("after_LFS_LOCK", (const void*)lfs, err);

    if (err) {
        /* Probe: record that the error branch is taken (should be impossible when LFS_LOCK is a no-op returning 0). */
        lfs_probe_log("LFS_LOCK_error_branch_taken", (const void*)lfs, err);
        return err;
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);

    /* Probe: before calling the internal implementation. */
    lfs_probe_log("before_lfs_fs_mkconsistent_", (const void*)lfs, 0);

    err = lfs_fs_mkconsistent_(lfs);

    /* Probe: record result from lfs_fs_mkconsistent_. */
    lfs_probe_log("after_lfs_fs_mkconsistent_", (const void*)lfs, err);

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);

    LFS_UNLOCK(lfs->cfg);

    /* Probe: record that unlock was executed. */
    lfs_probe_log("after_LFS_UNLOCK", (const void*)lfs, 0);

    return err;
}
