#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper - appends to /tmp/logs/872.txt. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/872.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    lfs_probe_log("ENTER lfs_fs_stat: lfs=%p fsinfo=%p", (void*)lfs, (void*)fsinfo);

    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log("After LFS_LOCK: err=%d cfg=%p", err, (void*)lfs->cfg);
    if (err) {
        lfs_probe_log("LFS_LOCK failed: returning %d", err);
        return err;
    }
    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    lfs_probe_log("Calling lfs_fs_stat_");
    err = lfs_fs_stat_(lfs, fsinfo);
    lfs_probe_log("Returned from lfs_fs_stat_: err=%d", err);

    LFS_TRACE("lfs_fs_stat -> %d", err);

    lfs_probe_log("Before LFS_UNLOCK: cfg=%p (about to call LFS_UNLOCK)", (void*)lfs->cfg);
    LFS_UNLOCK(lfs->cfg);
    /* Log after unlock to capture whether the unlock appears to be a no-op in this build. */
    lfs_probe_log("After LFS_UNLOCK: cfg=%p (note: may be no-op in this build)", (void*)lfs->cfg);

    lfs_probe_log("EXIT lfs_fs_stat: returning %d", err);
    return err;
}
