#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append human-readable control flow and state info
   to /tmp/logs/872.txt. Keep these functions static and small to avoid
   affecting the original logic other than adding logging side-effects. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/872.txt", "a");
    if (f == NULL) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static void probe_enter_fs_stat(void *lfs, void *fsinfo)
{
    probe_log("ENTER lfs_fs_stat: lfs=%p, fsinfo=%p", lfs, fsinfo);
}

static void probe_exit_fs_stat(int err, void *lfs, void *fsinfo)
{
    probe_log("EXIT lfs_fs_stat: err=%d, lfs=%p, fsinfo=%p", err, lfs, fsinfo);
}

int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    probe_enter_fs_stat((void*)lfs, (void*)fsinfo);

    int err = LFS_LOCK(lfs->cfg);
    probe_log("after LFS_LOCK: err=%d, lfs=%p, cfg=%p", err, (void*)lfs, (void*)lfs->cfg);
    if (err) {
        probe_log("early return from lfs_fs_stat due to lock error: %d", err);
        probe_exit_fs_stat(err, (void*)lfs, (void*)fsinfo);
        return err;
    }
    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);
    probe_log("after lfs_fs_stat_: err=%d, lfs=%p, fsinfo=%p", err, (void*)lfs, (void*)fsinfo);

    LFS_TRACE("lfs_fs_stat -> %d", err);
    probe_log("about to LFS_UNLOCK: lfs=%p, cfg=%p", (void*)lfs, (void*)lfs->cfg);
    LFS_UNLOCK(lfs->cfg);

    probe_exit_fs_stat(err, (void*)lfs, (void*)fsinfo);
    return err;
}
