#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Probe logging helper: writes human-readable trace lines to /tmp/logs/915.txt */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/915.txt", "a");
    if (f == NULL) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    /* Log before attempting to lock */
    lfs_probe_log("[probe] lfs_getattr called: lfs=%p, cfg=%p, path=\"%s\", type=%" PRIu8 ", buffer=%p, size=%" PRIu32",
            (void*)lfs, (void*)(lfs ? lfs->cfg : NULL), path ? path : "(null)", type, buffer, size);

    int err = LFS_LOCK(lfs->cfg);

    /* Probe the result of LFS_LOCK to demonstrate invariant behavior */
    lfs_probe_log("[probe] after LFS_LOCK: err=%d, cfg=%p", err, (void*)lfs->cfg);

    if (err) {
        lfs_probe_log("[probe] early return from lfs_getattr due to err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    lfs_probe_log("[probe] calling lfs_getattr_ (internal) with same args");
    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    LFS_TRACE("lfs_getattr -> %"PRId32, res);

    lfs_probe_log("[probe] lfs_getattr_ returned res=%" PRId32, res);

    LFS_UNLOCK(lfs->cfg);
    lfs_probe_log("[probe] called LFS_UNLOCK for cfg=%p", (void*)lfs->cfg);

    return res;
}
