#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <inttypes.h>

/* Probe logging helper — appends human-readable trace entries to /tmp/logs/912.txt */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/912.txt", "a");
    if (!f) {
        return; /* best-effort logging only */
    }

    time_t t = time(NULL);
    fprintf(f, "[%ld] ", (long)t);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log("ENTER lfs_getattr: lfs=%p path=\"%s\" type=%" PRIu8 " buffer=%p size=%" PRIu32 " LFS_LOCK_ret=%d",
            (void*)lfs, (path ? path : "(null)"), type, buffer, size, err);

    if (err) {
        lfs_probe_log("EXIT lfs_getattr (lock failed): err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    lfs_probe_log("CALL lfs_getattr_: lfs=%p path=\"%s\" type=%" PRIu8 " buffer=%p size=%" PRIu32,
            (void*)lfs, (path ? path : "(null)"), type, buffer, size);

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    lfs_probe_log("RETURN from lfs_getattr_: res=%" PRId32, res);
    LFS_TRACE("lfs_getattr -> %"PRId32, res);

    lfs_probe_log("About to call LFS_UNLOCK with cfg=%p", (void*)lfs->cfg);
    LFS_UNLOCK(lfs->cfg);
    lfs_probe_log("After LFS_UNLOCK call; returning res=%" PRId32, res);

    return res;
}
