#include <stdio.h>
#include <stdint.h>
#include <sys/stat.h>

/* Probe helpers to log control flow and state to /tmp/logs/957.txt */
static void lfs_mkdir_probe_ensure_dir(void) {
    /* best-effort create log directory; ignore errors */
    (void)mkdir("/tmp/logs", 0777);
}

static void lfs_mkdir_probe_log_ppath(const char *label, const char **ppath) {
    lfs_mkdir_probe_ensure_dir();
    FILE *f = fopen("/tmp/logs/957.txt", "a");
    if (!f) {
        return;
    }
    const char *path = (ppath != NULL) ? *ppath : NULL;
    /* Log the address of the pointer variable (ppath), the pointer value, and the pointed string */
    fprintf(f, "%s: ppath_addr=%p, path_value=%p, path_str=\"%s\"\n",
            label, (const void *)ppath, (const void *)path, path ? path : "(null)");
    fclose(f);
}

static void lfs_mkdir_probe_log_errid(const char *label, int err, uint16_t id) {
    lfs_mkdir_probe_ensure_dir();
    FILE *f = fopen("/tmp/logs/957.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "%s: err=%d, id=%u\n", label, err, (unsigned)id);
    fclose(f);
}

/* Original function with probes inserted to capture the violation scenario */
static int lfs_mkdir_(lfs_t *lfs, const char *path) {
    // deorphan if we haven't yet, needed at most once after poweron
    int err = lfs_fs_forceconsistency(lfs);
    if (err) {
        /* Log early exit due to forceconsistency error */
        lfs_mkdir_probe_log_errid("after_forceconsistency_return", err, 0);
        return err;
    }

    struct lfs_mlist cwd;
    cwd.next = lfs->mlist;
    uint16_t id;

    /* Log state BEFORE calling lfs_dir_find. We pass the address of the local
       'path' variable so the probe can log the pointer-to-pointer value. */
    lfs_mkdir_probe_log_ppath("before_lfs_dir_find", &path);

    err = lfs_dir_find(lfs, &cwd.m, &path, &id);

    /* Log state AFTER calling lfs_dir_find to capture any modification to the
       local parameter 'path' (i.e. evidence that *(&path) was assigned). */
    lfs_mkdir_probe_log_ppath("after_lfs_dir_find", &path);
    lfs_mkdir_probe_log_errid("lfs_dir_find_return", err, id);

    if (!(err == LFS_ERR_NOENT && lfs_path_islast(path))) {
        return (err < 0) ? err : LFS_ERR_EXIST;
    }

    // check that name fits
    lfs_size_t nlen = lfs_path_namelen(path);
    if (nlen > lfs->name_max) {
        return LFS_ERR_NAMETOOLONG;
    }

    // build up new directory
    lfs_alloc_ckpoint(lfs);
    lfs_mdir_t dir;
    err = lfs_dir_alloc(lfs, &dir);
    if (err) {
        return err;
    }

    // find end of list
    lfs_mdir_t pred = cwd.m;
    while (pred.split) {
        err = lfs_dir_fetch(lfs, &pred, pred.tail);
        if (err) {
            return err;
        }
    }

    // setup dir
    lfs_pair_tole32(pred.tail);
    err = lfs_dir_commit(lfs, &dir, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8), pred.tail}));
    lfs_pair_fromle32(pred.tail);
    if (err) {
        return err;
    }

    // current block not end of list?
    if (cwd.m.split) {
        // update tails, this creates a desync
        err = lfs_fs_preporphans(lfs, +1);
        if (err) {
            return err;
        }

        // it's possible our predecessor has to be relocated, and if
        // our parent is our predecessor's predecessor, this could have
        // caused our parent to go out of date, fortunately we can hook
        // ourselves into littlefs to catch this
        cwd.type = 0;
        cwd.id = 0;
        lfs->mlist = &cwd;

        lfs_pair_tole32(dir.pair);
        err = lfs_dir_commit(lfs, &pred, LFS_MKATTRS(
                {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8), dir.pair}));
        lfs_pair_fromle32(dir.pair);
        if (err) {
            lfs->mlist = cwd.next;
            return err;
        }

        lfs->mlist = cwd.next;
        err = lfs_fs_preporphans(lfs, -1);
        if (err) {
            return err;
        }
    }

    // now insert into our parent block
    lfs_pair_tole32(dir.pair);
    err = lfs_dir_commit(lfs, &cwd.m, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_CREATE, id, 0), NULL},
            {LFS_MKTAG(LFS_TYPE_DIR, id, nlen), path},
            {LFS_MKTAG(LFS_TYPE_DIRSTRUCT, id, 8), dir.pair},
            {LFS_MKTAG_IF(!cwd.m.split,
                LFS_TYPE_SOFTTAIL, 0x3ff, 8), dir.pair}));
    lfs_pair_fromle32(dir.pair);
    if (err) {
        return err;
    }

    /* Final log indicating successful mkdir and the final path pointer */
    lfs_mkdir_probe_log_ppath("mkdir_success_final_path", &path);

    return 0;
}
