#include <stdio.h>

/* Probe helper to log the state of the local parameter 'path' and its address.
   Writes to /tmp/logs/957.txt so other agents can reproduce the pointer-modification evidence.
*/
static void lfs_mkdir_probe(const char *stage, const char *path, const char **addr_of_path) {
    FILE *f = fopen("/tmp/logs/957.txt", "a");
    if (!f) {
        return;
    }

    /* Print: stage, address of the caller's local variable (i.e. &path),
       the current pointer value stored in that variable, and the value at *(&path)
       (which should be identical to the pointer value). This highlights whether
       the pointer variable itself was modified by a callee that received &path.
    */
    fprintf(f, "%s: addr_of_local_path=%p, local_path_value=%p, *addr_of_local_path=%p\n",
            stage,
            (const void *)addr_of_path,
            (const void *)path,
            (const void *)(addr_of_path ? *addr_of_path : (const char *)0));

    fclose(f);
}

static int lfs_mkdir_(lfs_t *lfs, const char *path) {
    // deorphan if we haven't yet, needed at most once after poweron
    int err = lfs_fs_forceconsistency(lfs);
    if (err) {
        return err;
    }

    struct lfs_mlist cwd;
    cwd.next = lfs->mlist;
    uint16_t id;

    /* Log state just before calling lfs_dir_find. We log the address of the local
       'path' variable (&path) and its current value so callers can detect whether
       a callee that receives &path changes the pointer itself. */
    lfs_mkdir_probe("before_lfs_dir_find", path, &path);

    err = lfs_dir_find(lfs, &cwd.m, &path, &id);

    /* Log state immediately after lfs_dir_find returns. If lfs_dir_find assigns to *path,
       this will show a different local_path_value or *addr_of_local_path compared to the
       previous log entry. */
    lfs_mkdir_probe("after_lfs_dir_find", path, &path);

    if (!(err == LFS_ERR_NOENT && lfs_path_islast(path))) {
        /* Log the decision point and the error that caused early return. */
        lfs_mkdir_probe("early_return_before_exist_check", path, &path);
        return (err < 0) ? err : LFS_ERR_EXIST;
    }

    // check that name fits
    lfs_size_t nlen = lfs_path_namelen(path);
    if (nlen > lfs->name_max) {
        lfs_mkdir_probe("return_nametooolong", path, &path);
        return LFS_ERR_NAMETOOLONG;
    }

    // build up new directory
    lfs_alloc_ckpoint(lfs);
    lfs_mdir_t dir;
    err = lfs_dir_alloc(lfs, &dir);
    if (err) {
        return err;
    }

    // find end of list
    lfs_mdir_t pred = cwd.m;
    while (pred.split) {
        err = lfs_dir_fetch(lfs, &pred, pred.tail);
        if (err) {
            return err;
        }
    }

    // setup dir
    lfs_pair_tole32(pred.tail);
    err = lfs_dir_commit(lfs, &dir, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8), pred.tail}));
    lfs_pair_fromle32(pred.tail);
    if (err) {
        return err;
    }

    // current block not end of list?
    if (cwd.m.split) {
        // update tails, this creates a desync
        err = lfs_fs_preporphans(lfs, +1);
        if (err) {
            return err;
        }

        // it's possible our predecessor has to be relocated, and if
        // our parent is our predecessor's predecessor, this could have
        // caused our parent to go out of date, fortunately we can hook
        // ourselves into littlefs to catch this
        cwd.type = 0;
        cwd.id = 0;
        lfs->mlist = &cwd;

        lfs_pair_tole32(dir.pair);
        err = lfs_dir_commit(lfs, &pred, LFS_MKATTRS(
                {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8), dir.pair}));
        lfs_pair_fromle32(dir.pair);
        if (err) {
            lfs->mlist = cwd.next;
            return err;
        }

        lfs->mlist = cwd.next;
        err = lfs_fs_preporphans(lfs, -1);
        if (err) {
            return err;
        }
    }

    /* Log right before committing the new directory entry that uses 'path' as data.
       This provides evidence of the value of the local pointer that is passed into
       the commit (and therefore whether it was changed previously).
    */
    lfs_mkdir_probe("before_final_dir_commit", path, &path);

    // now insert into our parent block
    lfs_pair_tole32(dir.pair);
    err = lfs_dir_commit(lfs, &cwd.m, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_CREATE, id, 0), NULL},
            {LFS_MKTAG(LFS_TYPE_DIR, id, nlen), path},
            {LFS_MKTAG(LFS_TYPE_DIRSTRUCT, id, 8), dir.pair},
            {LFS_MKTAG_IF(!cwd.m.split,
                LFS_TYPE_SOFTTAIL, 0x3ff, 8), dir.pair}));
    lfs_pair_fromle32(dir.pair);
    if (err) {
        lfs_mkdir_probe("after_final_dir_commit_error", path, &path);
        return err;
    }

    lfs_mkdir_probe("successful_return", path, &path);

    return 0;
}
