#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helpers: append logs to /tmp/logs/984.txt */
static void lfs_probe_log(FILE *f, const char *fmt, ...) {
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    fflush(f);
    va_end(ap);
}

static int lfs_mount_(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = lfs_init(lfs, cfg);
    if (err) {
        return err;
    }

    FILE *probe_f = fopen("/tmp/logs/984.txt", "a");
    if (probe_f) {
        lfs_probe_log(probe_f, "lfs_mount_ ENTRY: lfs=%p cfg=%p", (void*)lfs, (void*)cfg);
        lfs_probe_log(probe_f, "initial state: seed=%llu block_count=%llu name_max=%llu file_max=%llu attr_max=%llu inline_max=%llu",
                (unsigned long long)lfs->seed,
                (unsigned long long)lfs->block_count,
                (unsigned long long)lfs->name_max,
                (unsigned long long)lfs->file_max,
                (unsigned long long)lfs->attr_max,
                (unsigned long long)lfs->inline_max);
    }

    // scan directory blocks for superblock and any global updates
    lfs_mdir_t dir = {.tail = {0, 1}};
    struct lfs_tortoise_t tortoise = {
        .pair = {LFS_BLOCK_NULL, LFS_BLOCK_NULL},
        .i = 1,
        .period = 1,
    };
    while (!lfs_pair_isnull(dir.tail)) {
        if (probe_f) {
            lfs_probe_log(probe_f, "LOOP ITER START: dir.tail=(%llu,%llu) dir.pair=(%llu,%llu) tortoise.pair=(%llu,%llu) tortoise.i=%llu tortoise.period=%llu",
                    (unsigned long long)dir.tail[0], (unsigned long long)dir.tail[1],
                    (unsigned long long)dir.pair[0], (unsigned long long)dir.pair[1],
                    (unsigned long long)tortoise.pair[0], (unsigned long long)tortoise.pair[1],
                    (unsigned long long)tortoise.i, (unsigned long long)tortoise.period);
        }

        err = lfs_tortoise_detectcycles(&dir, &tortoise);
        if (probe_f) {
            lfs_probe_log(probe_f, "lfs_tortoise_detectcycles returned: %d", err);
        }
        if (err < 0) {
            goto cleanup;
        }

        // fetch next block in tail list
        lfs_stag_t tag = lfs_dir_fetchmatch(lfs, &dir, dir.tail,
                LFS_MKTAG(0x7ff, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_SUPERBLOCK, 0, 8),
                NULL,
                lfs_dir_find_match, &(struct lfs_dir_find_match){
                    lfs, "littlefs", 8});
        if (probe_f) {
            lfs_probe_log(probe_f, "lfs_dir_fetchmatch returned tag=%lld", (long long)tag);
        }
        if (tag < 0) {
            err = tag;
            goto cleanup;
        }

        // has superblock?
        if (tag && !lfs_tag_isdelete(tag)) {
            if (probe_f) {
                lfs_probe_log(probe_f, "SUPERBLOCK FOUND: tag=%lld dir.pair=(%llu,%llu)",
                        (long long)tag, (unsigned long long)dir.pair[0], (unsigned long long)dir.pair[1]);
            }

            // update root
            lfs->root[0] = dir.pair[0];
            lfs->root[1] = dir.pair[1];

            // grab superblock
            lfs_superblock_t superblock;
            tag = lfs_dir_get(lfs, &dir, LFS_MKTAG(0x7ff, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_INLINESTRUCT, 0, sizeof(superblock)),
                    &superblock);
            if (tag < 0) {
                err = tag;
                goto cleanup;
            }
            lfs_superblock_fromle32(&superblock);

            // check version
            uint16_t major_version = (0xffff & (superblock.version >> 16));
            uint16_t minor_version = (0xffff & (superblock.version >>  0));
            if (probe_f) {
                lfs_probe_log(probe_f, "SUPERBLOCK VERSION: major=%u minor=%u stored_version=(0x%08llx)",
                        (unsigned)major_version, (unsigned)minor_version, (unsigned long long)superblock.version);
            }
            if (major_version != lfs_fs_disk_version_major(lfs)
                    || minor_version > lfs_fs_disk_version_minor(lfs)) {
                LFS_ERROR("Invalid version "
                        "v%"PRIu16".%"PRIu16" != v%"PRIu16".%"PRIu16,
                        major_version,
                        minor_version,
                        lfs_fs_disk_version_major(lfs),
                        lfs_fs_disk_version_minor(lfs));
                err = LFS_ERR_INVAL;
                goto cleanup;
            }

            // found older minor version? set an in-device only bit in the
            // gstate so we know we need to rewrite the superblock before
            // the first write
            bool needssuperblock = false;
            if (minor_version < lfs_fs_disk_version_minor(lfs)) {
                LFS_DEBUG("Found older minor version "
                        "v%"PRIu16".%"PRIu16" < v%"PRIu16".%"PRIu16,
                        major_version,
                        minor_version,
                        lfs_fs_disk_version_major(lfs),
                        lfs_fs_disk_version_minor(lfs));
                needssuperblock = true;
            }
            // note this bit is reserved on disk, so fetching more gstate
            // will not interfere here
            lfs_fs_prepsuperblock(lfs, needssuperblock);

            if (probe_f) {
                lfs_probe_log(probe_f, "needssuperblock=%d", needssuperblock ? 1 : 0);
            }

            // check superblock configuration
            if (superblock.name_max) {
                if (superblock.name_max > lfs->name_max) {
                    LFS_ERROR("Unsupported name_max (%%"PRIu32" > %%"PRIu32")",
                            superblock.name_max, lfs->name_max);
                    err = LFS_ERR_INVAL;
                    goto cleanup;
                }

                lfs->name_max = superblock.name_max;
                if (probe_f) {
                    lfs_probe_log(probe_f, "Updated name_max=%llu", (unsigned long long)lfs->name_max);
                }
            }

            if (superblock.file_max) {
                if (superblock.file_max > lfs->file_max) {
                    LFS_ERROR("Unsupported file_max (%%"PRIu32" > %%"PRIu32")",
                            superblock.file_max, lfs->file_max);
                    err = LFS_ERR_INVAL;
                    goto cleanup;
                }

                lfs->file_max = superblock.file_max;
                if (probe_f) {
                    lfs_probe_log(probe_f, "Updated file_max=%llu", (unsigned long long)lfs->file_max);
                }
            }

            if (superblock.attr_max) {
                if (superblock.attr_max > lfs->attr_max) {
                    LFS_ERROR("Unsupported attr_max (%%"PRIu32" > %%"PRIu32")",
                            superblock.attr_max, lfs->attr_max);
                    err = LFS_ERR_INVAL;
                    goto cleanup;
                }

                lfs->attr_max = superblock.attr_max;

                // we also need to update inline_max in case attr_max changed
                lfs->inline_max = lfs_min(lfs->inline_max, lfs->attr_max);
                if (probe_f) {
                    lfs_probe_log(probe_f, "Updated attr_max=%llu inline_max=%llu", (unsigned long long)lfs->attr_max, (unsigned long long)lfs->inline_max);
                }
            }

            // this is where we get the block_count from disk if block_count=0
            if (lfs->cfg->block_count
                    && superblock.block_count != lfs->cfg->block_count) {
                LFS_ERROR("Invalid block count (%%"PRIu32" != %%"PRIu32")",
                        superblock.block_count, lfs->cfg->block_count);
                err = LFS_ERR_INVAL;
                goto cleanup;
            }

            lfs->block_count = superblock.block_count;
            if (probe_f) {
                lfs_probe_log(probe_f, "Set block_count=%llu", (unsigned long long)lfs->block_count);
            }

            if (superblock.block_size != lfs->cfg->block_size) {
                LFS_ERROR("Invalid block size (%%"PRIu32" != %%"PRIu32")",
                        superblock.block_size, lfs->cfg->block_size);
                err = LFS_ERR_INVAL;
                goto cleanup;
            }
        }

        // has gstate?
        err = lfs_dir_getgstate(lfs, &dir, &lfs->gstate);
        if (probe_f) {
            lfs_probe_log(probe_f, "lfs_dir_getgstate returned: %d gstate.tag=%llu gstate.pair=(%llu,%llu)",
                    err, (unsigned long long)lfs->gstate.tag,
                    (unsigned long long)lfs->gstate.pair[0], (unsigned long long)lfs->gstate.pair[1]);
        }
        if (err) {
            goto cleanup;
        }
    }

    // update littlefs with gstate
    if (!lfs_gstate_iszero(&lfs->gstate)) {
        LFS_DEBUG("Found pending gstate 0x%08"PRIx32"%08"PRIx32"%08"PRIx32,
                lfs->gstate.tag,
                lfs->gstate.pair[0],
                lfs->gstate.pair[1]);
    }
    lfs->gstate.tag += !lfs_tag_isvalid(lfs->gstate.tag);
    lfs->gdisk = lfs->gstate;

    // setup free lookahead, to distribute allocations uniformly across
    // boots, we start the allocator at a random location
    lfs->lookahead.start = lfs->seed % lfs->block_count;
    lfs_alloc_drop(lfs);

    if (probe_f) {
        lfs_probe_log(probe_f, "EXIT SUCCESS: gstate.tag=%llu gstate.pair=(%llu,%llu) lookahead.start=%llu",
                (unsigned long long)lfs->gstate.tag,
                (unsigned long long)lfs->gstate.pair[0], (unsigned long long)lfs->gstate.pair[1],
                (unsigned long long)lfs->lookahead.start);
        fclose(probe_f);
    }

    return 0;

cleanup:
    if (probe_f) {
        lfs_probe_log(probe_f, "CLEANUP: err=%d final_root=(%llu,%llu) gstate.tag=%llu gstate.pair=(%llu,%llu)",
                err, (unsigned long long)lfs->root[0], (unsigned long long)lfs->root[1],
                (unsigned long long)lfs->gstate.tag,
                (unsigned long long)lfs->gstate.pair[0], (unsigned long long)lfs->gstate.pair[1]);
    }
    lfs_unmount_(lfs);
    if (probe_f) {
        fclose(probe_f);
    }
    return err;
}
