#include <stdio.h>

/* Probe helpers to log control flow and state for the lfs_dir_find_match
 * call-site. These helpers try to avoid changing any logic and use
 * const-qualified pointer parameters where possible to avoid introducing
 * additional pointer constness violations.
 */
static void lfs_probe_log_match(const struct lfs_dir_find_match *match) {
    FILE *f = fopen("/tmp/logs/983.txt", "a");
    if (!f) {
        return;
    }
    /* Log the pointer, the lfs pointer stored, the name string and length.
     * Cast pointers to void* for portable %p printing.
     */
    fprintf(f, "[probe] lfs_dir_find_match PARAM at %p: lfs=%p, name=\"%s\", name_len=%u\n",
            (void*)match, (void*)match->lfs,
            match->name ? match->name : "(null)", (unsigned)match->name_len);
    fclose(f);
}

