#include <stdio.h>
#include <stdarg.h>

static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/1002.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_remove_(lfs_t *lfs, const char *path) {
    lfs_log_probe("[lfs_remove_] ENTER path=%s", (path ? path : "(null)"));

    // deorphan if we haven't yet, needed at most once after poweron
    int err = lfs_fs_forceconsistency(lfs);
    lfs_log_probe("[lfs_remove_] lfs_fs_forceconsistency returned %d", err);
    if (err) {
        lfs_log_probe("[lfs_remove_] EXIT early due to forceconsistency err=%d", err);
        return err;
    }

    lfs_mdir_t cwd;
    lfs_stag_t tag = lfs_dir_find(lfs, &cwd, &path, NULL);
    lfs_log_probe("[lfs_remove_] lfs_dir_find returned tag=0x%llx", (unsigned long long)tag);
    if (tag < 0 || lfs_tag_id(tag) == 0x3ff) {
        int ret = (tag < 0) ? (int)tag : LFS_ERR_INVAL;
        lfs_log_probe("[lfs_remove_] EXIT invalid tag: tag=%lld id=%u ret=%d",
                (long long)tag, (unsigned)lfs_tag_id(tag), ret);
        return ret;
    }

    struct lfs_mlist dir;
    dir.next = lfs->mlist;
    lfs_log_probe("[lfs_remove_] saved mlist next=%p", (void *)dir.next);
    if (lfs_tag_type3(tag) == LFS_TYPE_DIR) {
        lfs_log_probe("[lfs_remove_] target is a directory (tag=0x%llx)", (unsigned long long)tag);

        // must be empty before removal
        lfs_block_t pair[2];
        lfs_stag_t res = lfs_dir_get(lfs, &cwd, LFS_MKTAG(0x700, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_STRUCT, lfs_tag_id(tag), 8), pair);
        lfs_log_probe("[lfs_remove_] lfs_dir_get returned res=0x%llx", (unsigned long long)res);
        if (res < 0) {
            lfs_log_probe("[lfs_remove_] EXIT due to dir_get err=%lld", (long long)res);
            return (int)res;
        }
        lfs_pair_fromle32(pair);

        err = lfs_dir_fetch(lfs, &dir.m, pair);
        lfs_log_probe("[lfs_remove_] lfs_dir_fetch returned err=%d count=%u split=%u pair=[%llu,%llu]",
                err, (unsigned)dir.m.count, (unsigned)dir.m.split,
                (unsigned long long)pair[0], (unsigned long long)pair[1]);
        if (err) {
            lfs_log_probe("[lfs_remove_] EXIT due to dir_fetch err=%d", err);
            return err;
        }

        if (dir.m.count > 0 || dir.m.split) {
            lfs_log_probe("[lfs_remove_] EXIT not empty: count=%u split=%u", (unsigned)dir.m.count, (unsigned)dir.m.split);
            return LFS_ERR_NOTEMPTY;
        }

        // mark fs as orphaned
        lfs_log_probe("[lfs_remove_] before lfs_fs_preporphans(+1): gstate.tag=%llu",
                (unsigned long long)lfs->gstate.tag);
        err = lfs_fs_preporphans(lfs, +1);
        lfs_log_probe("[lfs_remove_] after lfs_fs_preporphans(+1): err=%d gstate.tag=%llu",
                err, (unsigned long long)lfs->gstate.tag);
        if (err) {
            lfs_log_probe("[lfs_remove_] EXIT unexpected non-zero from lfs_fs_preporphans(+1) err=%d", err);
            return err;
        }

        // I know it's crazy but yes, dir can be changed by our parent's
        // commit (if predecessor is child)
        dir.type = 0;
        dir.id = 0;
        lfs->mlist = &dir;
        lfs_log_probe("[lfs_remove_] set lfs->mlist to dir (%p)", (void *)lfs->mlist);
    }

    // delete the entry
    err = lfs_dir_commit(lfs, &cwd, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_DELETE, lfs_tag_id(tag), 0), NULL}));
    lfs_log_probe("[lfs_remove_] lfs_dir_commit returned err=%d", err);
    if (err) {
        lfs->mlist = dir.next;
        lfs_log_probe("[lfs_remove_] commit failed, restored mlist to %p and EXIT err=%d", (void *)lfs->mlist, err);
        return err;
    }

    lfs->mlist = dir.next;
    lfs_log_probe("[lfs_remove_] restored lfs->mlist to %p", (void *)lfs->mlist);
    if (lfs_gstate_hasorphans(&lfs->gstate)) {
        LFS_ASSERT(lfs_tag_type3(tag) == LFS_TYPE_DIR);

        // fix orphan
        lfs_log_probe("[lfs_remove_] gstate has orphans, before lfs_fs_preporphans(-1): gstate.tag=%llu",
                (unsigned long long)lfs->gstate.tag);
        err = lfs_fs_preporphans(lfs, -1);
        lfs_log_probe("[lfs_remove_] after lfs_fs_preporphans(-1): err=%d gstate.tag=%llu",
                err, (unsigned long long)lfs->gstate.tag);
        if (err) {
            lfs_log_probe("[lfs_remove_] EXIT unexpected non-zero from lfs_fs_preporphans(-1) err=%d", err);
            return err;
        }

        err = lfs_fs_pred(lfs, dir.m.pair, &cwd);
        lfs_log_probe("[lfs_remove_] lfs_fs_pred returned err=%d pair=[%llu,%llu]",
                err, (unsigned long long)dir.m.pair[0], (unsigned long long)dir.m.pair[1]);
        if (err) {
            lfs_log_probe("[lfs_remove_] EXIT due to fs_pred err=%d", err);
            return err;
        }

        err = lfs_dir_drop(lfs, &cwd, &dir.m);
        lfs_log_probe("[lfs_remove_] lfs_dir_drop returned err=%d", err);
        if (err) {
            lfs_log_probe("[lfs_remove_] EXIT due to dir_drop err=%d", err);
            return err;
        }
    }

    lfs_log_probe("[lfs_remove_] EXIT success");
    return 0;
}
