#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper. Appends to /tmp/logs/3047.txt. */
static void
mhd_probe_log(const char *stage,
              const void *ctx_ptr,
              const uint8_t *data_ptr,
              size_t length,
              unsigned int bytes_have)
{
  FILE *f = fopen("/tmp/logs/3047.txt", "a");
  if (f == NULL)
    return;
  fprintf(f, "STAGE: %s\n", stage);
  fprintf(f, "  ctx=%p data=%p length=%zu bytes_have=%u\n",
          ctx_ptr, (const void *)data_ptr, length, bytes_have);
  fclose(f);
}

void
MHD_SHA512_256_update (struct Sha512_256Ctx *ctx,
                       const uint8_t *data,
                       size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  uint64_t count_hi; /**< The high part to be moved to another variable */

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length) {
    mhd_probe_log("early-return-zero-length", ctx, data, length, 0);
    return; /* Shortcut, do nothing */
  }
#endif /* ! MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA512_256_BLOCK_SIZE-1))
           equals (count % SHA512_256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (SHA512_256_BLOCK_SIZE - 1));
  mhd_probe_log("entry-after-bytes_have-calc", ctx, data, length, bytes_have);

  ctx->count += length;
#if SIZEOF_SIZE_T > 7
  if (length > ctx->count)
    ctx->count_bits_hi += 1U << 3; /* Value wrap */
#endif /* SIZEOF_SIZE_T > 7 */
  count_hi = ctx->count >> 61;
  if (0 != count_hi)
  {
    ctx->count_bits_hi += count_hi;
    ctx->count &= UINT64_C (0x1FFFFFFFFFFFFFFF);
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = SHA512_256_BLOCK_SIZE - bytes_have;
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      mhd_probe_log("combine-buffer-before-memcpy", ctx, data, length, bytes_have);
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      /* Violation point: pointer parameter 'data' is modified below. */
      mhd_probe_log("about-to-modify-parameter-data-before", ctx, data, length, bytes_have);
      data += bytes_left;
      length -= bytes_left;
      mhd_probe_log("modified-parameter-data-after", ctx, data, length, 0);
      sha512_256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
    }
  }

  while (SHA512_256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    mhd_probe_log("loop-before-transform", ctx, data, length, 0);
    sha512_256_transform (ctx->H, data);
    mhd_probe_log("loop-after-transform-before-modify", ctx, data, length, 0);
    data += SHA512_256_BLOCK_SIZE; /* Pointer parameter modification recorded */
    length -= SHA512_256_BLOCK_SIZE;
    mhd_probe_log("loop-after-modify", ctx, data, length, 0);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    mhd_probe_log("final-copy-before-memcpy", ctx, data, length, bytes_have);
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
    mhd_probe_log("final-copy-after-memcpy", ctx, data, length, bytes_have);
  }
}
